import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { ICatalogEvidence } from "@ORGANIZATION/PROJECT-api/lib/structures/ICatalogEvidence";
import { IPageICatalogEvidence } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageICatalogEvidence";
import typia from "typia";

export async function test_list_catalog_evidence_with_various_filters_and_pagination(connection: api.IConnection): Promise<void> {
  // 1. Basic: Fetch with no filters (default page/limit)
  const basicReq = {};
  const basicRes = await api.functional.productCatalog.catalogEvidence.patch(connection, { body: basicReq });
  typia.assert<IPageICatalogEvidence>(basicRes);
  TestValidator.predicate("Basic: data array exists")(() => Array.isArray(basicRes.data));

  // 2. Pagination boundary: page=1, limit=1
  const pagedReq = { page: 1, limit: 1 };
  const pagedRes = await api.functional.productCatalog.catalogEvidence.patch(connection, { body: pagedReq });
  typia.assert<IPageICatalogEvidence>(pagedRes);
  TestValidator.predicate("Pagination: correct page size")(() => pagedRes.data.length <= 1);
  TestValidator.equals("Pagination: page==1")<number>(pagedReq.page!)(pagedRes.pagination.current);
  TestValidator.equals("Pagination: limit==1")<number>(pagedReq.limit!)(pagedRes.pagination.limit);

  // 3. Filtering by description (if at least one record exists)
  if (basicRes.data.length > 0) {
    const desc = basicRes.data[0].description;
    const descReq = { description: desc };
    const descRes = await api.functional.productCatalog.catalogEvidence.patch(connection, { body: descReq });
    typia.assert<IPageICatalogEvidence>(descRes);
    TestValidator.predicate("Filter: All match description")(() => descRes.data.every(ev => ev.description === desc));
  }

  // 4. Filtering by reference_uri (if any present)
  const firstRefUri = basicRes.data.find(ev => !!ev.reference_uri)?.reference_uri;
  if (firstRefUri) {
    const refUriReq = { reference_uri: firstRefUri };
    const refUriRes = await api.functional.productCatalog.catalogEvidence.patch(connection, { body: refUriReq });
    typia.assert<IPageICatalogEvidence>(refUriRes);
    TestValidator.predicate("Filter: All match reference_uri")(() => refUriRes.data.every(ev => ev.reference_uri === firstRefUri));
  }

  // 5. Filtering by catalog_event_id (if any present)
  const firstEventId = basicRes.data.find(ev => !!ev.catalog_event_id)?.catalog_event_id;
  if (firstEventId) {
    const eventIdReq = { catalog_event_id: firstEventId };
    const eventIdRes = await api.functional.productCatalog.catalogEvidence.patch(connection, { body: eventIdReq });
    typia.assert<IPageICatalogEvidence>(eventIdRes);
    TestValidator.predicate("Filter: All match catalog_event_id")(() => eventIdRes.data.every(ev => ev.catalog_event_id === firstEventId));
  }

  // 6. Created_at range filtering (if possible)
  if (basicRes.data.length > 0) {
    const anyEvidence = basicRes.data[0];
    const createdReq = {
      created_at_from: anyEvidence.created_at,
      created_at_to: anyEvidence.created_at
    };
    const createdRes = await api.functional.productCatalog.catalogEvidence.patch(connection, { body: createdReq });
    typia.assert<IPageICatalogEvidence>(createdRes);
    TestValidator.predicate("Filter: All created_at within single timestamp")(() =>
      createdRes.data.every(ev => ev.created_at === anyEvidence.created_at)
    );
  }

  // 7. Empty result (random UUID for catalog_event_id)
  const randomUUID = "00000000-0000-4000-8000-000000000001"; // unlikely to exist
  const emptyReq = { catalog_event_id: randomUUID };
  const emptyRes = await api.functional.productCatalog.catalogEvidence.patch(connection, { body: emptyReq });
  typia.assert<IPageICatalogEvidence>(emptyRes);
  TestValidator.equals("Empty: No results")<ICatalogEvidence[]>([])(emptyRes.data);

  // 8. Page out of bounds (very high page number)
  const outOfBoundsReq = { page: 999999, limit: 1 };
  const outOfBoundsRes = await api.functional.productCatalog.catalogEvidence.patch(connection, { body: outOfBoundsReq });
  typia.assert<IPageICatalogEvidence>(outOfBoundsRes);
  TestValidator.equals("Out of bounds: No data")<ICatalogEvidence[]>([])(outOfBoundsRes.data);

  // 9. Invalid filter field in body (should reject)
  await TestValidator.error("Invalid field triggers error")(() =>
    // @ts-expect-error: sending unknown field
    api.functional.productCatalog.catalogEvidence.patch(connection, { body: { bad_field: "x" } })
  );

  // 10. Null for page/limit/no filter (should not error)
  const nullPageReq = { page: null, limit: null };
  const nullPageRes = await api.functional.productCatalog.catalogEvidence.patch(connection, { body: nullPageReq });
  typia.assert<IPageICatalogEvidence>(nullPageRes);
  TestValidator.predicate("Null page/limit accepted")(() => Array.isArray(nullPageRes.data));
}

