    };
    const specialResult = await api.functional.productCatalog.catalogEvidence.post(connection, specialInput);
    typia.assert<ICatalogEvidence>(specialResult);
    TestValidator.equals("reference_uri - special")(specialInput.reference_uri)(specialResult.reference_uri);
    TestValidator.equals("description - special")(specialInput.description)(specialResult.description);

    // 4. Negative: missing reference_uri
    const missingRef: ICatalogEvidence.ICreate = {
