import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { ICatalogEvidence } from "@ORGANIZATION/PROJECT-api/lib/structures/ICatalogEvidence";
import typia from "typia";

import { randomUUID } from "crypto";

export async function test_api_update_catalog_evidence_by_id_with_validation_and_not_found_case(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create a catalog evidence record
  const createInput: ICatalogEvidence.ICreate = {
    reference_uri: "https://fileserver.example.com/evidence/file1.pdf",
    description: "Initial evidence for compliance audit.",
    catalog_event_id: null,
  };
  const created = await api.functional.productCatalog.catalogEvidence.post(
    connection,
    createInput,
  );
  typia.assert<ICatalogEvidence>(created);

  // 2. Valid update on allowed field (description)
  const updateInput: ICatalogEvidence.IUpdate = {
    description: "Corrected description for compliance purposes.",
  };
  const updated = await api.functional.productCatalog.catalogEvidence.put(
    connection,
    created.id,
    updateInput,
  );
  typia.assert<ICatalogEvidence>(updated);
  TestValidator.equals("id should remain unchanged")(created.id)(updated.id);
  TestValidator.equals("reference_uri is immutable")(created.reference_uri)(updated.reference_uri);
  TestValidator.equals("catalog_event_id remains unchanged")(created.catalog_event_id)(updated.catalog_event_id);
  TestValidator.equals("description updated")(updateInput.description)(updated.description);
  TestValidator.equals("created_at is immutable")(created.created_at)(updated.created_at);

  // 3. Update with non-existent ID (expect 404)
  await TestValidator.httpError("Should fail with not found", 404)(async () => {
    await api.functional.productCatalog.catalogEvidence.put(
      connection,
      randomUUID(),
      updateInput,
    );
  });

  // 4. Invalid update: empty description (if not allowed)
  await TestValidator.httpError("Should fail with invalid description", 400, 422)(async () => {
    await api.functional.productCatalog.catalogEvidence.put(
      connection,
      created.id,
      { description: "" },
    );
  });

  // 5. Invalid update: attempt to mutate an immutable field. (the API schema does not support this field, so this should fail type check or be ignored)
  // We'll pass an extra field and expect that the API ignores/rejects it or throws a validation error.
  await TestValidator.httpError("Should reject immutable field mutation", 400, 409, 422)(async () => {
    // @ts-expect-error: purposely adding invalid field for test
    await api.functional.productCatalog.catalogEvidence.put(
      connection,
      created.id,
      {
        description: "Some new description.",
        reference_uri: "https://forbidden-update.example.com/file2.pdf",
      },
    );
  });
}

