import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { ICart } from "@ORGANIZATION/PROJECT-api/lib/structures/ICart";
import { IPageICart } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageICart";
import typia from "typia";

export async function test_api_list_carts_with_filters_and_deleted_cart_handling(
  connection: api.IConnection
): Promise<void> {
  // 1. Create cart records for two users and two sessions
  const userA = "11111111-1111-4111-8111-111111111111";
  const userB = "22222222-2222-4222-8222-222222222222";
  const session1 = "33333333-3333-4333-8333-333333333333";
  const session2 = "44444444-4444-4444-8444-444444444444";

  const carts: ICart[] = [];
  // Cart 1: userA, session1
  carts.push(
    await api.functional.cartOrder.cart.post(connection, {
      user_id: userA,
      session_id: session1,
    })
  );
  // Cart 2: userB, session2
  carts.push(
    await api.functional.cartOrder.cart.post(connection, {
      user_id: userB,
      session_id: session2,
    })
  );
  // Cart 3: userA, session2
  carts.push(
    await api.functional.cartOrder.cart.post(connection, {
      user_id: userA,
      session_id: session2,
    })
  );

  // Cart 4: userB, no session
  carts.push(
    await api.functional.cartOrder.cart.post(connection, {
      user_id: userB,
      session_id: null,
    })
  );

  // 2. Soft-delete Cart 2
  const deletedCart = carts[1];
  const deleteResult = await api.functional.cartOrder.cart.delete(
    connection,
    deletedCart.id
  );
  typia.assert(deleteResult);
  TestValidator.equals("deleteResult.id")(deleteResult.id)(deletedCart.id);
  TestValidator.predicate("deleteResult.deleted_at is valid")(() =>
    typeof deleteResult.deleted_at === "string"
  );

  // 3. Basic list: should not include soft-deleted cart
  const res1 = await api.functional.cartOrder.cart.patch(connection, {
    page: 1,
    limit: 10,
  });
  typia.assert<IPageICart>(res1);
  TestValidator.predicate("res1.data does not include deleted cart")(
    () => res1.data.every((c) => c.id !== deletedCart.id)
  );

  // 4. Filter by user_id (userA):
  const res2 = await api.functional.cartOrder.cart.patch(connection, {
    user_id: userA,
    page: 1,
    limit: 10,
  });
  typia.assert<IPageICart>(res2);
  TestValidator.predicate("res2 All belong to userA")(
    () => res2.data.every((c) => c.user_id === userA)
  );

  // 5. Filter by session_id (session2):
  const res3 = await api.functional.cartOrder.cart.patch(connection, {
    session_id: session2,
    page: 1,
    limit: 10,
  });
  typia.assert<IPageICart>(res3);
  TestValidator.predicate("res3 All session_id are session2")(
    () => res3.data.every((c) => c.session_id === session2)
  );

  // 6. Pagination (limit):
  const res4 = await api.functional.cartOrder.cart.patch(connection, {
    page: 1,
    limit: 2,
  });
  typia.assert<IPageICart>(res4);
  TestValidator.predicate("res4 pagination.limit is 2")(
    () => res4.pagination.limit === 2
  );
  TestValidator.predicate("res4.data.length <= 2")(
    () => res4.data.length <= 2
  );

  // 7. Soft-deleted: default query must not show deleted
  TestValidator.predicate("No deleted carts in default list")(
    () => res1.data.every((c) => !c.deleted_at)
  );

  // 8. Query deleted carts only (deleted_at_exists=true):
  const res5 = await api.functional.cartOrder.cart.patch(connection, {
    deleted_at_exists: true,
    page: 1,
    limit: 10,
  });
  typia.assert<IPageICart>(res5);
  TestValidator.predicate("res5 all carts are deleted")(
    () => res5.data.every((c) => c.deleted_at)
  );

  // 9. Query active (non-deleted) only (deleted_at_exists=false):
  const res6 = await api.functional.cartOrder.cart.patch(connection, {
    deleted_at_exists: false,
    page: 1,
    limit: 10,
  });
  typia.assert<IPageICart>(res6);
  TestValidator.predicate("res6 all carts are active")(
    () => res6.data.every((c) => !c.deleted_at)
  );

  // 10. Query with filter to yield no results
  const res7 = await api.functional.cartOrder.cart.patch(connection, {
    user_id: "99999999-aaaa-4aaa-8aaa-aaaaaaaaaaaa",
    page: 1,
    limit: 10,
  });
  typia.assert<IPageICart>(res7);
  TestValidator.equals("empty data")([] as ICart[])(res7.data);

  // 11. Error case: Invalid filter (malformed UUID)
  await TestValidator.httpError("invalid user_id filter error")(400)(async () => {
    await api.functional.cartOrder.cart.patch(connection, {
      user_id: "not-a-uuid",
      page: 1,
      limit: 10,
    });
  });
}

