import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { ICart } from "@ORGANIZATION/PROJECT-api/lib/structures/ICart";
import typia from "typia";

export async function test_api_cart_create_with_valid_invalid_and_duplicate_constraints(connection: api.IConnection): Promise<void> {
    // 1. Minimal valid data (user_id only)
    const minimalUserId = "1c75b11f-489d-4b6e-81db-2bedb7958b88";
    const minimalInput: ICart.ICreate = {
        user_id: minimalUserId,
    };
    const cart1 = await api.functional.cart.post(connection, minimalInput);
    typia.assert<ICart>(cart1);
    TestValidator.equals("user_id for minimal cart")(minimalInput.user_id)(cart1.user_id);
    TestValidator.predicate("session_id should be null for minimal")(cart1.session_id === null || cart1.session_id === undefined);

    // 2. Maximal valid data (user_id + session_id)
    const maximalUserId = "5a0af2ea-b9c6-4848-b8da-11e764b5f0ca";
    const maximalSessionId = "baf2a3f8-fb23-43db-8032-9f9b7e3e31ad";
    const maximalInput: ICart.ICreate = {
        user_id: maximalUserId,
        session_id: maximalSessionId,
    };
    const cart2 = await api.functional.cart.post(connection, maximalInput);
    typia.assert<ICart>(cart2);
    TestValidator.equals("user_id for maximal cart")(maximalInput.user_id)(cart2.user_id);
    TestValidator.equals("session_id for maximal cart")(maximalInput.session_id)(cart2.session_id);

    // 3. Missing required fields (omit user_id)
    await TestValidator.httpError("cart requires user_id, expect 400")(400)(
        async () => {
            // Omitting user_id will violate required
            const invalidPayload = { session_id: "b08a3290-9ae8-48ea-88ef-166a0d12d49c" } as any;
            await api.functional.cart.post(connection, invalidPayload);
        }
    );

    // 4. Invalid data types (numeric user_id, malformed session_id)
    await TestValidator.httpError("numeric user_id should fail", 400)(400)(
        async () => {
            const invalidInput = { user_id: 12345 } as any;
            await api.functional.cart.post(connection, invalidInput);
        }
    );
    await TestValidator.httpError("malformed session_id should fail", 400)(400)(
        async () => {
            const invalidInput = { user_id: minimalUserId, session_id: "not-a-uuid" } as any;
            await api.functional.cart.post(connection, invalidInput);
        }
    );

    // 5. Duplicate cart creation (should 409)
    await TestValidator.httpError("duplicate active cart for same user", 409)(409)(
        async () => {
            // minimalUserId already has cart1
            await api.functional.cart.post(connection, minimalInput);
        }
    );
}

import { TestValidator } from "@nestia/e2e";
