export async function test_update_cart_metadata_and_handle_invalid_deleted_case(
  connection: api.IConnection,
): Promise<void> {
  // Step 1: Create valid cart (simulate authenticated user, deterministic UUID)
  const userId = "49370dbd-bfcb-4819-b1b4-88be45c7b09b";
  const createInput: ICart.ICreate = {
    user_id: userId,
    session_id: null,
  };
  const cart = await api.functional.cartOrder.cart.post(connection, createInput);
  typia.assert<ICart>(cart);
  TestValidator.equals("user_id matches")(createInput.user_id)(cart.user_id);
  TestValidator.equals("session_id null")(createInput.session_id)(cart.session_id);

  // Step 2: Valid update - change session_id and updated_at
  const newSessionId = "17288cd2-9dc8-4257-8c3e-cc0585f17306";
  const updateTime = new Date().toISOString();
  const updateInp: ICart.IUpdate = {
    session_id: newSessionId,
    updated_at: updateTime,
  };
  const updated = await api.functional.cartOrder.cart.put(connection, cart.id, updateInp);
  typia.assert<ICart>(updated);
  TestValidator.equals("id match")(cart.id)(updated.id);
  TestValidator.equals("session_id updated")(newSessionId)(updated.session_id);
  TestValidator.equals("updated_at match")(updateTime)(updated.updated_at);

  // Step 3: Update non-existent cart
  await TestValidator.httpError("404 on non-existent cart")(404)(
    () => api.functional.cartOrder.cart.put(connection, "99999999-aaaa-bbbb-cccc-eeeeeeeeeeee", updateInp)
  );

  // Step 4: Simulate deleted cart, update should fail (if API uses deleted_at logic, try update after marking as deleted)
  // Try updating cart after logical deletion (simulate deletion by updating deleted_at if updatable, else skip to expected error)
  // Here: set deleted_at directly is likely not allowed via ICart.IUpdate, so simulate as if the cart is deleted.
