import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { ICartOrderCartItem } from "@ORGANIZATION/PROJECT-api/lib/structures/ICartOrderCartItem";
import { ICartItem } from "@ORGANIZATION/PROJECT-api/lib/structures/ICartItem";
import { IPageICartItem } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageICartItem";
import typia from "typia";

export async function test_list_cart_items_with_filtering_and_empty_or_invalid_filters(connection: api.IConnection): Promise<void> {
    // Test Data Setup
    const cartId1 = "00000000-0000-4000-8000-000000000001";  // deterministic fake UUID
    const cartId2 = "00000000-0000-4000-8000-000000000002";
    const variantId1 = "00000000-0000-4000-8000-000000000101";
    const variantId2 = "00000000-0000-4000-8000-000000000102";

    // Add items
    const item1 = await api.functional.cartOrder.cartItem.post(connection, {
        cart_id: cartId1,
        product_variant_id: variantId1,
        quantity: 2
    });
    const item2 = await api.functional.cartOrder.cartItem.post(connection, {
        cart_id: cartId1,
        product_variant_id: variantId2,
        quantity: 1
    });
    const item3 = await api.functional.cartOrder.cartItem.post(connection, {
        cart_id: cartId2,
        product_variant_id: variantId1,
        quantity: 3
    });

    // --- Positive: List all (no filter) ---
    const allList = await api.functional.cartOrder.cartItem.patch(connection, {});
    typia.assert<IPageICartItem>(allList);
    TestValidator.predicate("allList covers inserted items")(() => {
        const ids = allList.data.map(x => x.id);
        return ids.includes(item1.id) && ids.includes(item2.id) && ids.includes(item3.id);
    });

    // --- Positive: Filter by cart_id ---
    const byCart1 = await api.functional.cartOrder.cartItem.patch(connection, {
        cart_id: cartId1
    });
    typia.assert<IPageICartItem>(byCart1);
    TestValidator.predicate("byCart1 items must all belong to cartId1")(() =>
        byCart1.data.every(x => x.cart_id === cartId1)
    );
    TestValidator.equals("byCart1.ids")(byCart1.data.map(x => x.id).sort())([item1.id, item2.id].sort());

    // --- Positive: Filter by product_variant_id ---
    const byVariant1 = await api.functional.cartOrder.cartItem.patch(connection, {
        product_variant_id: variantId1
    });
    typia.assert<IPageICartItem>(byVariant1);
    TestValidator.predicate("byVariant1 items must all be variantId1")(() =>
        byVariant1.data.every(x => x.product_variant_id === variantId1)
    );
    TestValidator.equals("byVariant1.ids")(byVariant1.data.map(x => x.id).sort())([item1.id, item3.id].sort());

    // --- Pagination test (limit=1) ---
    const paged = await api.functional.cartOrder.cartItem.patch(connection, {
        limit: 1,
        page: 1
    });
    typia.assert<IPageICartItem>(paged);
    TestValidator.equals("pagination.limit")(paged.pagination.limit)(1);
    TestValidator.equals("pagination.current")(paged.pagination.current)(1);
    TestValidator.predicate("paged.data.length=1")(() => paged.data.length === 1);

    // --- Negative: Non-existent cart_id ---
    const emptyCart = await api.functional.cartOrder.cartItem.patch(connection, {
        cart_id: "99999999-9999-4999-8999-999999999999"
    });
    typia.assert<IPageICartItem>(emptyCart);
    TestValidator.equals("emptyCart.data")<ICartOrderCartItem[]>([])(emptyCart.data);

    // --- Negative: Invalid product_variant_id ---
    const emptyVariant = await api.functional.cartOrder.cartItem.patch(connection, {
        product_variant_id: "99999999-9999-4999-8999-888888888888"
    });
    typia.assert<IPageICartItem>(emptyVariant);
    TestValidator.equals("emptyVariant.data")<ICartOrderCartItem[]>([])(emptyVariant.data);

    // --- Negative: Unreachable page (e.g. page > total pages) ---
    const unreachable = await api.functional.cartOrder.cartItem.patch(connection, {
        page: 1000
    });
    typia.assert<IPageICartItem>(unreachable);
    TestValidator.equals("unreachable.data")<ICartOrderCartItem[]>([])(unreachable.data);
}

