export async function test_add_cart_item_with_valid_and_invalid_foreign_key_and_constraints(connection: api.IConnection): Promise<void> {
    // 1. Create a cart (require a user_id)
    const valid_user_id = randomUUID();
    const cartInput: ICart.ICreate = {
        user_id: valid_user_id,
        session_id: null,
    };
    const cart = await api.functional.cartOrder.cart.post(connection, cartInput);
    typia.assert<ICart>(cart);
    TestValidator.equals("user_id association")(cartInput.user_id)(cart.user_id);

    // 2. Add a valid cart item
    const valid_product_variant_id = randomUUID();
    const valid_quantity = 2;
    const itemInput: ICartOrderCartItem.ICreate = {
        cart_id: cart.id,
        product_variant_id: valid_product_variant_id,
        quantity: valid_quantity,
    };
    const item = await api.functional.cartOrder.cartItem.post(connection, itemInput);
    typia.assert<ICartOrderCartItem>(item);
    TestValidator.equals("association: cart_id")(cart.id)(item.cart_id);
    TestValidator.equals("product_variant_id")(itemInput.product_variant_id)(item.product_variant_id);
    TestValidator.equals("quantity")(itemInput.quantity)(item.quantity);

    // 3a. Negative quantity
    const negQuantityInput: ICartOrderCartItem.ICreate = {
        cart_id: cart.id,
        product_variant_id: valid_product_variant_id,
        quantity: -5,
    };
    await TestValidator.httpError("negative quantity should fail")(400)(() => 
        api.functional.cartOrder.cartItem.post(connection, negQuantityInput)
    );

    // 3b. Invalid product_variant_id (not a valid UUID or not existing)
    const fake_product_variant_id = "00000000-0000-0000-0000-000000000000";
    const invalidVariantInput: ICartOrderCartItem.ICreate = {
        cart_id: cart.id,
        product_variant_id: fake_product_variant_id,
        quantity: 1,
    };
    await TestValidator.httpError("invalid product_variant_id should fail")(400,409)(() => 
        api.functional.cartOrder.cartItem.post(connection, invalidVariantInput)
    );

    // 3c. Missing required field (quantity)
    // @ts-expect-error: purposely omit quantity
    const missingQuantityInput = {
        cart_id: cart.id,
        product_variant_id: valid_product_variant_id,
        // quantity missing
    };
    await TestValidator.httpError("missing quantity should fail")(400)(() => 
        api.functional.cartOrder.cartItem.post(connection, missingQuantityInput)
    );

    // 4. Non-existent cart (foreign key violation)
    const nonExistentCartInput: ICartOrderCartItem.ICreate = {
        cart_id: randomUUID(), // random new UUID, not in db
        product_variant_id: valid_product_variant_id,
        quantity: 1,
    };
    await TestValidator.httpError("non-existent cart_id should fail")(400,409)(() =>
        api.functional.cartOrder.cartItem.post(connection, nonExistentCartInput)
    );
}

