export async function test_update_cart_item_data_with_invalid_and_nonexistent_cases(connection: api.IConnection): Promise<void> {
  // 1. Create a valid cart item as prerequisite
  const cartItemInput: ICartOrderCartItem.ICreate = {
    cart_id: "4e8bbcee-6bb7-411c-98be-17f9f7eac273", // use fixed, valid UUIDs for test
