  TestValidator.equals("cart_id")(cartItemInput.cart_id)(created.cart_id);
  TestValidator.equals("product_variant_id")(cartItemInput.product_variant_id)(created.product_variant_id);
  TestValidator.equals("quantity")(cartItemInput.quantity)(created.quantity);

  // 2. Attempt a valid update (quantity)
  const update1: ICartOrderCartItem.IUpdate = { quantity: 5 };
  const updated = await api.functional.cartOrder.cartItem.put(
    connection,
