import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { ICartOrderOrder } from "@ORGANIZATION/PROJECT-api/lib/structures/ICartOrderOrder";
import { IPageICartOrderOrder } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageICartOrderOrder";
import typia from "typia";

export async function test_list_orders_with_various_filters_and_pagination(connection: api.IConnection): Promise<void> {
  // Step 1: Create multiple orders
  const userA = "00000000-0000-0000-0000-00000000a001";
  const userB = "00000000-0000-0000-0000-00000000b002";
  const cartA = "00000000-0000-0000-0000-00000000c101";
  const cartB = "00000000-0000-0000-0000-00000000c202";

  // Create 4 orders for various filter scenarios
  const orderInputs: ICartOrderOrder.ICreate[] = [
    { user_id: userA, cart_id: cartA },
    { user_id: userA, cart_id: cartB },
    { user_id: userB, cart_id: cartA },
    { user_id: userB, cart_id: cartB },
  ];
  const createdOrders: ICartOrderOrder[] = [];
  for (const input of orderInputs) {
    const order = await api.functional.cartOrder.order.post(connection, input);
    typia.assert<ICartOrderOrder>(order);
    createdOrders.push(order);
  }

  // Wait a bit to ensure created_at timestamps differ
  await new Promise((res) => setTimeout(res, 10));

  // Step 2: List orders by user filter
  const userARequest = { user_id: userA };
  const pageUserA = await api.functional.cartOrder.order.patch(connection, { body: userARequest });
  typia.assert<IPageICartOrderOrder>(pageUserA);
  TestValidator.predicate("orders match userA")(pageUserA.data.every((o) => o.user_id === userA));

  // Step 3: List orders by cart_id filter
  const cartARequest = { cart_id: cartA };
  const pageCartA = await api.functional.cartOrder.order.patch(connection, { body: cartARequest });
  typia.assert<IPageICartOrderOrder>(pageCartA);
  TestValidator.predicate("orders match cartA")(pageCartA.data.every((o) => o.cart_id === cartA));

  // Step 4: List orders with both user and cart filters (should be at most one)
  const bothRequest = { user_id: userB, cart_id: cartB };
  const pageBoth = await api.functional.cartOrder.order.patch(connection, { body: bothRequest });
  typia.assert<IPageICartOrderOrder>(pageBoth);
  TestValidator.equals("exact 1 record for userB-cartB")([createdOrders[3]])(pageBoth.data);

  // Step 5: List orders by created_at time window
  const recentOrder = createdOrders[createdOrders.length - 1];
  const timeWindowRequest = {
    created_after: recentOrder.created_at,
    limit: 10,
  };
  const pageTime = await api.functional.cartOrder.order.patch(connection, { body: timeWindowRequest });
  typia.assert<IPageICartOrderOrder>(pageTime);
  TestValidator.equals("orders after latest created_at")([] as ICartOrderOrder[])(pageTime.data);

  // Step 6: Pagination - limit/page combinations
  const pagedRequest = { limit: 2, page: 1 };
  const page1 = await api.functional.cartOrder.order.patch(connection, { body: pagedRequest });
  typia.assert<IPageICartOrderOrder>(page1);
  TestValidator.equals("pagination: page1")(2)(page1.data.length);
  TestValidator.predicate("pagination meta correct")(() => page1.pagination.current === 1 && page1.pagination.limit === 2);
  // page: 2
  const page2 = await api.functional.cartOrder.order.patch(connection, { body: { ...pagedRequest, page: 2 } });
  typia.assert<IPageICartOrderOrder>(page2);
  TestValidator.equals("pagination: page2")(2)(page2.data.length);
  // page: above max
  const pageMax = await api.functional.cartOrder.order.patch(connection, { body: { ...pagedRequest, page: 3 } });
  typia.assert<IPageICartOrderOrder>(pageMax);
  TestValidator.equals("pagination: pageMax")([] as ICartOrderOrder[])(pageMax.data);

  // Step 7: Non-existent filter values
  const noOrderRequest = { user_id: "ffffffff-ffff-ffff-ffff-ffffffffffff" };
  const pageNoOrder = await api.functional.cartOrder.order.patch(connection, { body: noOrderRequest });
  typia.assert<IPageICartOrderOrder>(pageNoOrder);
  TestValidator.equals("empty for non-existent user")([] as ICartOrderOrder[])(pageNoOrder.data);

  // Step 8: Invalid input types -> expect error
  await TestValidator.httpError("negative page")(400)(() =>
    api.functional.cartOrder.order.patch(connection, { body: { page: -1 } })
  );
  await TestValidator.httpError("bad limit")([400, 422])(() =>
    api.functional.cartOrder.order.patch(connection, { body: { limit: "bad" as any } })
  );
}

