import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { ICartOrderOrder } from "@ORGANIZATION/PROJECT-api/lib/structures/ICartOrderOrder";
import typia from "typia";

export async function test_api_update_order_fields_with_validation_and_immutability(connection: api.IConnection): Promise<void> {
    // 1. Create a valid order
    const newOrderInput: ICartOrderOrder.ICreate = {
        user_id: "00000000-0000-0000-0000-000000000001",
        cart_id: "00000000-0000-0000-0000-000000000002",
    };
    const created = await api.functional.cartOrder.order.post(connection, newOrderInput);
    typia.assert<ICartOrderOrder>(created);
    const orderId = created.id;

    // 2. Update mutable field(s) using PUT /cartOrder/order/{id}
    const updatePayload: ICartOrderOrder.IUpdate = {
        updated_at: new Date().toISOString(),
    };
    const updated = await api.functional.cartOrder.order.put(connection, orderId, updatePayload);
    typia.assert<ICartOrderOrder>(updated);
    TestValidator.equals("order id should not change")(orderId)(updated.id);
    TestValidator.equals("user_id should not change")(created.user_id)(updated.user_id);
    TestValidator.equals("cart_id should not change")(created.cart_id)(updated.cart_id);
    TestValidator.predicate("updated_at should update")(() => updated.updated_at !== created.updated_at);
    TestValidator.equals("deleted_at field unchanged")(created.deleted_at)(updated.deleted_at);

    // 3. Attempt update on non-existent order id
    const nonExistentId = "ffffffff-ffff-ffff-ffff-ffffffffffff";
    await TestValidator.httpError("update non-existent order should fail")(404)(
        () => api.functional.cartOrder.order.put(connection, nonExistentId, updatePayload)
    );

    // 4. Attempt update with missing required fields
    await TestValidator.error("missing required 'updated_at' field should fail")(
        () => api.functional.cartOrder.order.put(connection, orderId, {} as any)
    );

    // 5. Attempt to update an immutable field (should be ignored or rejected)
    const invalidUpdate: any = { updated_at: new Date().toISOString(), user_id: "malicious-value" };
    await TestValidator.error("should not allow immutable field mutation")(
        () => api.functional.cartOrder.order.put(connection, orderId, invalidUpdate)
    );
}

