import { IPageICartOrderOrderItem } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageICartOrderOrderItem";
import typia from "typia";

export async function test_list_order_items_with_search_and_soft_deleted_behavior(connection: api.IConnection): Promise<void> {
  // Prepare common fields
  const orderId1 = "11111111-1111-1111-1111-111111111111";
  const orderId2 = "22222222-2222-2222-2222-222222222222";
  const variantIdA = "aaaaaaaa-aaaa-aaaa-aaaa-aaaaaaaaaaaa";
  const variantIdB = "bbbbbbbb-bbbb-bbbb-bbbb-bbbbbbbbbbbb";

  // Create multiple order_item records
  const orderItem1 = await api.functional.cartOrder.orderItem.post(connection, {
    order_id: orderId1,
    product_variant_id: variantIdA,
    quantity: 2,
  });
  typia.assert<ICartOrderOrderItem>(orderItem1);

  const orderItem2 = await api.functional.cartOrder.orderItem.post(connection, {
    order_id: orderId1,
    product_variant_id: variantIdB,
    quantity: 3,
  });
  typia.assert<ICartOrderOrderItem>(orderItem2);

  const orderItem3 = await api.functional.cartOrder.orderItem.post(connection, {
    order_id: orderId2,
    product_variant_id: variantIdA,
    quantity: 1,
  });
  typia.assert<ICartOrderOrderItem>(orderItem3);

  // 1. List all items (no filters):
  const listAll = await api.functional.cartOrder.orderItem.patch(connection, {});
  typia.assert<IPageICartOrderOrderItem>(listAll);
  // All created items must be found in the list
  const allIds = listAll.data.map(x => x.id);
  TestValidator.predicate("all inserted order_item records should appear")(() =>
    [orderItem1.id, orderItem2.id, orderItem3.id].every(id => allIds.includes(id))
  );

  // 2. Filter by order_id
  const byOrder = await api.functional.cartOrder.orderItem.patch(connection, { order_id: orderId1 });
  typia.assert<IPageICartOrderOrderItem>(byOrder);
  TestValidator.predicate("only order items with order_id1")(() =>
    byOrder.data.every(x => x.order_id === orderId1)
  );
  TestValidator.predicate("contains only the correct subset")(() =>
    [orderItem1.id, orderItem2.id].every(id => byOrder.data.some(x => x.id === id)) &&
    byOrder.data.length === 2
  );

  // 3. Filter by product_variant_id
  const byVariant = await api.functional.cartOrder.orderItem.patch(connection, { product_variant_id: variantIdA });
  typia.assert<IPageICartOrderOrderItem>(byVariant);
  TestValidator.predicate("all items with product_variant_id A")(() =>
    byVariant.data.every(x => x.product_variant_id === variantIdA)
  );
  TestValidator.predicate("contains orderItem1 and orderItem3")(() =>
    [orderItem1.id, orderItem3.id].every(id => byVariant.data.some(x => x.id === id)) &&
    byVariant.data.length === 2
  );

  // 4. Filter with no matches
  const noMatch = await api.functional.cartOrder.orderItem.patch(connection, { order_id: "deadbeef-dead-beef-dead-beefdeadbeef" });
  typia.assert<IPageICartOrderOrderItem>(noMatch);
  TestValidator.equals("empty result for non-existent order_id")([] as ICartOrderOrderItem[])(noMatch.data);

  // 5. Attempt invalid filter (quantity_range with invalid values)
  await TestValidator.error("invalid quantity_range filter should cause error")(() =>
    api.functional.cartOrder.orderItem.patch(connection, { quantity_range: [5] })
  );

  // 6. (Soft-delete test skipped: API does not support soft-deletion or relevant flag on order items in described spec)
}

