    };
    const item: ICartOrderOrderItem = await api.functional.cartOrder.orderItem.post(connection, itemInput);
    typia.assert<ICartOrderOrderItem>(item);
    TestValidator.equals("order_id")(itemInput.order_id)(item.order_id);
    TestValidator.equals("product_variant_id")(itemInput.product_variant_id)(item.product_variant_id);
    TestValidator.equals("quantity")(itemInput.quantity)(item.quantity);

    // 3. Missing required field: quantity
    await TestValidator.httpError("missing quantity")(400)(async () => {
        // @ts-expect-error purposely omitting quantity
        await api.functional.cartOrder.orderItem.post(connection, {
            order_id: order.id,
            product_variant_id: "20000000-0000-0000-0000-000000000001"
        });
    });

    // 4. Invalid references - invalid order_id and product_variant_id
