        await api.functional.cartOrder.orderItem.post(connection, itemInput);
    });

    // 6. Invalid quantity (zero and negative)
    for (const badQty of [0, -1]) {
        await TestValidator.httpError(`invalid quantity: ${badQty}`)(400, 422)(async () => {
            await api.functional.cartOrder.orderItem.post(connection, {
                ...itemInput,
                quantity: badQty,
                // Use a different product_variant_id to avoid duplicate error
                product_variant_id: `20000000-0000-0000-0000-0000000000${10 + badQty}`
            });
        });
    }
}

