import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IOrderStatus } from "@ORGANIZATION/PROJECT-api/lib/structures/IOrderStatus";
import { IPageIOrderStatus } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIOrderStatus";
import typia from "typia";

export async function test_list_order_status_with_pagination_and_various_filters(connection: api.IConnection): Promise<void> {
  // 1. Create test order_status records with varied data
  const now = new Date();
  const isoNow = now.toISOString();
  const order1 = crypto.randomUUID();
  const order2 = crypto.randomUUID();
  const actor1 = crypto.randomUUID();
  const actor2 = crypto.randomUUID();

  // Create records
  const records: IOrderStatus[] = [];
  records.push(
    await api.functional.cartOrder.orderStatus.post(connection, {
      order_id: order1,
      status_code: "pending",
      changed_at: isoNow,
      changed_by: actor1,
    })
  );
  records.push(
    await api.functional.cartOrder.orderStatus.post(connection, {
      order_id: order1,
      status_code: "paid",
      changed_at: new Date(now.getTime() + 60000).toISOString(),
      changed_by: actor2,
    })
  );
  records.push(
    await api.functional.cartOrder.orderStatus.post(connection, {
      order_id: order2,
      status_code: "shipped",
      changed_at: new Date(now.getTime() + 120000).toISOString(),
      changed_by: null,
    })
  );

  // 2. List all records (no filters)
  let page = await api.functional.cartOrder.orderStatus.patch(connection, {});
  typia.assert<IPageIOrderStatus>(page);
  TestValidator.predicate("must contain created records")(() =>
    records.every(x => page.data.some(y => y.id === x.id))
  );

  // 3. Filter by order_id
  page = await api.functional.cartOrder.orderStatus.patch(connection, { order_id: order1 });
  typia.assert<IPageIOrderStatus>(page);
  TestValidator.equals("should only include selected order_id records")(
    records.filter(x => x.order_id === order1).map(x => x.id).sort()
  )(
    page.data.map(x => x.id).sort()
  );

  // 4. Filter by status_code
  page = await api.functional.cartOrder.orderStatus.patch(connection, { status_code: "paid" });
  typia.assert<IPageIOrderStatus>(page);
  TestValidator.equals("should include only 'paid' status")([
    records.find(x => x.status_code === "paid")!.id
  ])(
    page.data.map(x => x.id)
  );

  // 5. Filter by changed_at_range
  const start = now.toISOString();
  const end = new Date(now.getTime() + 70000).toISOString();
  page = await api.functional.cartOrder.orderStatus.patch(connection, { changed_at_range: [start, end] });
  typia.assert<IPageIOrderStatus>(page);
  TestValidator.equals("should include only records in time range")(
    records.slice(0, 2).map(x => x.id).sort()
  )(
    page.data.map(x => x.id).sort()
  );

  // 6. Filter with nonsense status_code
  page = await api.functional.cartOrder.orderStatus.patch(connection, { status_code: "nonsense-code" });
  typia.assert<IPageIOrderStatus>(page);
  TestValidator.equals("should return no records for nonsense filter")(
    [] as IOrderStatus[]
  )(
    page.data
  );

  // 7. Pagination with limit 2
  page = await api.functional.cartOrder.orderStatus.patch(connection, { status_code: undefined, limit: 2 });
  typia.assert<IPageIOrderStatus>(page);
  TestValidator.equals("pagination.limit = 2")(
    2
  )(
    page.data.length
  );
  TestValidator.equals("pagination.current should be 1")(
    1
  )(
    page.pagination.current
  );

  // 8. (Sorting cannot be tested unless the API exposes a sort param; assume default is changed_at ascending)
  TestValidator.equals("sorted by changed_at ascending")(
    page.data.map(x => x.changed_at).sort()
  )(
    page.data.map(x => x.changed_at)
  );

  // 9. Empty payload (should return all)
  page = await api.functional.cartOrder.orderStatus.patch(connection, {});
  typia.assert<IPageIOrderStatus>(page);
  TestValidator.equals("should include all records for empty query")(
    records.map(x => x.id).sort()
  )(
    page.data.map(x => x.id).sort()
  );

  // 10. Error handling for invalid filter property (simulate with random field)
  await TestValidator.httpError("rejects extraneous fields")(400)(async () =>
    // @ts-expect-error
    api.functional.cartOrder.orderStatus.patch(connection, { random_field: "foo" })
  );
}

