import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia from "typia";
import { IFavoritesPersonalization } from "@ORGANIZATION/PROJECT-api/lib/structures/IFavoritesPersonalization";

export async function test_search_and_paginate_personalization_events_with_various_filters(
    connection: api.IConnection,
): Promise<void> {
    // Create sample events with varying filterable fields
    const userIdA = "11111111-1111-1111-1111-111111111111";
    const userIdB = "22222222-2222-2222-2222-222222222222";
    const productId = "33333333-3333-3333-3333-333333333333";
    const recId = "44444444-4444-4444-4444-444444444444";
    const modelVer = "vA.1";
    const now = new Date();

    const makeEvent = async (overrides: Partial<IFavoritesPersonalization.IPersonalizationEventCreate> & {timestamp?: string}) => {
      const base: IFavoritesPersonalization.IPersonalizationEventCreate = {
        user_id: userIdA,
        recommendation_id: recId,
        product_id: productId,
        event_type: "view",
        model_version: modelVer,
        context: "web:chrome:en"
      };
      const event = { ...base, ...overrides };
      // Simulate time by context if necessary
      const created = await api.functional.favoritesPersonalization.personalizationEvents.post(connection, event);
      typia.assert<IFavoritesPersonalization.IPersonalizationEvent>(created);
      return created;
    };

    // Event for userA/view/recent
    const eventA = await makeEvent({});
    // Event for userB/click/earlier
    const aDayAgo = new Date(now.getTime() - 86400000).toISOString();
    const eventB = await makeEvent({ user_id: userIdB, event_type: "click", model_version: "vB.1", context: "mobile:safari:kr" });
    // Event for userA/dismiss/specific timestamp
    const eventC = await makeEvent({ event_type: "dismiss", model_version: "vA.2", context: null });

    // 1. Filter by user_id
    let search1: IFavoritesPersonalization.IPersonalizationEventSearchRequest = {
      user_id: userIdA,
    };
    const res1 = await api.functional.favoritesPersonalization.personalizationEvents.patch(connection, search1);
    typia.assert<IFavoritesPersonalization.PageIPersonalizationEvent>(res1);
    TestValidator.predicate("all user_id matched")(() => res1.data.every(e => e.user_id === userIdA));

    // 2. Filter by event_type
    let search2: IFavoritesPersonalization.IPersonalizationEventSearchRequest = {
      event_type: "click"
    };
    const res2 = await api.functional.favoritesPersonalization.personalizationEvents.patch(connection, search2);
    typia.assert<IFavoritesPersonalization.PageIPersonalizationEvent>(res2);
    TestValidator.predicate("all event_type matched")(() => res2.data.every(e => e.event_type === "click"));

    // 3. Filter by user_id + event_type
    let search3: IFavoritesPersonalization.IPersonalizationEventSearchRequest = {
      user_id: userIdA,
      event_type: "dismiss"
    };
    const res3 = await api.functional.favoritesPersonalization.personalizationEvents.patch(connection, search3);
    typia.assert<IFavoritesPersonalization.PageIPersonalizationEvent>(res3);
    TestValidator.predicate("all user_id and event_type matched")(() => res3.data.every(e => e.user_id === userIdA && e.event_type === "dismiss"));

    // 4. Filter by nonexistent user_id (should return empty)
    let search4: IFavoritesPersonalization.IPersonalizationEventSearchRequest = {
      user_id: "99999999-9999-9999-9999-999999999999"
    };
    const res4 = await api.functional.favoritesPersonalization.personalizationEvents.patch(connection, search4);
    typia.assert<IFavoritesPersonalization.PageIPersonalizationEvent>(res4);
    TestValidator.equals("no results/empty array")([] as IFavoritesPersonalization.IPersonalizationEvent[])(res4.data);

    // 5. Filter by date range: before now
    let search5: IFavoritesPersonalization.IPersonalizationEventSearchRequest = {
      timestamp_from: new Date(Date.now() - 2 * 86400000).toISOString(), // 2 days ago
      timestamp_to: new Date().toISOString()
    };
    const res5 = await api.functional.favoritesPersonalization.personalizationEvents.patch(connection, search5);
    typia.assert<IFavoritesPersonalization.PageIPersonalizationEvent>(res5);
    TestValidator.predicate("all timestamps inside range")(() => res5.data.every(e => new Date(e.timestamp) >= new Date(search5.timestamp_from!) && new Date(e.timestamp) <= new Date(search5.timestamp_to!)));

    // 6. Pagination: limit=1
    let search6: IFavoritesPersonalization.IPersonalizationEventSearchRequest = { limit: 1 };
    const res6 = await api.functional.favoritesPersonalization.personalizationEvents.patch(connection, search6);
    typia.assert<IFavoritesPersonalization.PageIPersonalizationEvent>(res6);
    TestValidator.equals("page size == 1")(1)(res6.data.length);
    TestValidator.equals("pagination.limit == 1")(1)(res6.pagination.limit);

    // 7. Pagination: page=100
    let search7: IFavoritesPersonalization.IPersonalizationEventSearchRequest = { page: 100, limit: 1 };
    const res7 = await api.functional.favoritesPersonalization.personalizationEvents.patch(connection, search7);
    typia.assert<IFavoritesPersonalization.PageIPersonalizationEvent>(res7);
    TestValidator.equals("empty data at over-paginated page")([] as IFavoritesPersonalization.IPersonalizationEvent[])(res7.data);
    TestValidator.equals("pagination properties exist")("current" in res7.pagination && "pages" in res7.pagination)(true);
}

