export async function test_create_personalization_event_with_valid_and_invalid_payload(
  connection: api.IConnection
): Promise<void> {
  // 1. Prepare valid payload
default values
  const validPayload: IFavoritesPersonalization.IPersonalizationEventCreate = {
    user_id: "123e4567-e89b-12d3-a456-426614174000",
    recommendation_id: "223e4567-e89b-12d3-a456-426614174001",
    product_id: "323e4567-e89b-12d3-a456-426614174002",
    event_type: "click",
    model_version: "v1.2.3",
    context: "mobile"
  };

  // 2. Successful creation
  const created = await api.functional.favoritesPersonalization.personalizationEvents.post(
    connection,
    validPayload
  );
  typia.assert<IFavoritesPersonalization.IPersonalizationEvent>(created);

  // 3. Assert fields match
  TestValidator.equals("user_id")(validPayload.user_id)(created.user_id);
  TestValidator.equals("recommendation_id")(validPayload.recommendation_id)(created.recommendation_id);
  TestValidator.equals("product_id")(validPayload.product_id)(created.product_id);
  TestValidator.equals("event_type")(validPayload.event_type)(created.event_type);
  TestValidator.equals("model_version")(validPayload.model_version)(created.model_version);
  TestValidator.equals("context")(validPayload.context)(created.context);
  TestValidator.predicate("id should be UUID")(() => typeof created.id === "string" && created.id.length >= 30);
  TestValidator.predicate("timestamp should be RFC3339 string")(() => typeof created.timestamp === "string" && created.timestamp.length > 10);

  // 4. Test context=null case
  const payloadWithNullContext: IFavoritesPersonalization.IPersonalizationEventCreate = {
    ...validPayload,
    context: null
  };
  const createdNullContext = await api.functional.favoritesPersonalization.personalizationEvents.post(
    connection, payloadWithNullContext
  );
  typia.assert<IFavoritesPersonalization.IPersonalizationEvent>(createdNullContext);
  TestValidator.equals("context=null")(null)(createdNullContext.context);

  // 5. Invalid payloads -- missing required fields
  const missingFieldsCases: Partial<IFavoritesPersonalization.IPersonalizationEventCreate>[] = [
    { ...validPayload, user_id: undefined },
    { ...validPayload, recommendation_id: undefined },
    { ...validPayload, product_id: undefined },
    { ...validPayload, event_type: undefined },
    { ...validPayload, model_version: undefined },
  ];
  for (const casePayload of missingFieldsCases) {
    await TestValidator.httpError("missing field")
      (400)
      (() =>
        api.functional.favoritesPersonalization.personalizationEvents.post(
          connection,
          casePayload as any
        )
      );
  }

  // 6. Invalid user_id (not a UUID)
  const invalidUserIdPayload = { ...validPayload, user_id: "not-a-uuid" };
  await TestValidator.httpError("invalid user_id format")(400)(() =>
    api.functional.favoritesPersonalization.personalizationEvents.post(
      connection,
      invalidUserIdPayload
    )
  );

  // 7. Invalid event_type (empty string)
  const invalidEventTypePayload = { ...validPayload, event_type: "" };
  await TestValidator.httpError("invalid event_type")(400)(() =>
    api.functional.favoritesPersonalization.personalizationEvents.post(
      connection,
      invalidEventTypePayload
    )
  );
}

import { TestValidator } from "@nestia/e2e";
