export async function test_api_update_personalization_event_by_id_with_valid_and_invalid_conditions(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create event for update
  const eventCreate: IFavoritesPersonalization.IPersonalizationEventCreate = {
    user_id: "11111111-1111-1111-1111-111111111111",
    recommendation_id: "22222222-2222-2222-2222-222222222222",
    product_id: "33333333-3333-3333-3333-333333333333",
    event_type: "view",
    model_version: "1.0.0",
    context: "web/kr/seoul",
  };
  const created = await api.functional.favoritesPersonalization.personalizationEvents.post(
    connection,
    eventCreate,
  );
  typia.assert<IFavoritesPersonalization.IPersonalizationEvent>(created);

  // 2. Update with valid changes
  const updatedData: IPersonalizationEvent.IUpdate = {
    event_type: "click",
    model_version: "1.0.1",
    context: "mobile/kr/seoul",
    timestamp: new Date().toISOString(),
    product_id: created.product_id,
    recommendation_id: created.recommendation_id,
  };
  const updated = await api.functional.favoritesPersonalization.personalizationEvents.put(
    connection,
    created.id,
    updatedData,
  );
  typia.assert<IPersonalizationEvent>(updated);
  TestValidator.equals("updated event_type")("click")(updated.event_type);
  TestValidator.equals("updated model_version")("1.0.1")(updated.model_version);
  TestValidator.equals("updated context")("mobile/kr/seoul")(updated.context);
  TestValidator.equals("updated timestamp")(updatedData.timestamp)(updated.timestamp);

  // 3. Try with invalid/non-existent ID
  await TestValidator.httpError("invalid id 404") (404)(async () => {
    await api.functional.favoritesPersonalization.personalizationEvents.put(
      connection,
      "99999999-9999-9999-9999-999999999999",
      updatedData
    );
  });

  // 4. Try with invalid input (missing model_version)
  const invalidData: any = { ...updatedData };
  delete invalidData.model_version;
  await TestValidator.httpError("missing required field", 400, 422)(async () => {
    await api.functional.favoritesPersonalization.personalizationEvents.put(
      connection,
      created.id,
      invalidData,
    );
  });
}

