export async function test_search_and_paginate_recommendations_with_filters(connection: api.IConnection): Promise<void> {
  // Step 1: Create recommendations with different combinations
  const recA = await api.functional.favoritesPersonalization.recommendations.post(connection, {
    user_id: "11111111-1111-1111-1111-111111111111",
    product_id: "22222222-2222-2222-2222-222222222222",
