  const recB = await api.functional.favoritesPersonalization.recommendations.post(connection, {
    user_id: "33333333-3333-3333-3333-333333333333",
    product_id: "22222222-2222-2222-2222-222222222222",
    model_id: "model-y",
    model_version: "v1.1",
    rationale: "Recommended based on your interest",
    recommendation_time: "2024-06-23T01:00:00.000Z",
  });
