  const recC = await api.functional.favoritesPersonalization.recommendations.post(connection, {
    user_id: "11111111-1111-1111-1111-111111111111",
    product_id: "44444444-4444-4444-4444-444444444444",
    model_id: "model-x",
    model_version: "v1.2",
    rationale: null,
    recommendation_time: "2024-06-23T04:00:00.000Z",
  });
  typia.assert<IRecommendation>(recC);

  // Step 2: Filter by user_id
  const byUser = await api.functional.favoritesPersonalization.recommendations.patch(connection, {
    user_id: "11111111-1111-1111-1111-111111111111",
    limit: 10,
    offset: 0
  });
  typia.assert<IPageIRecommendation>(byUser);
  TestValidator.predicate("user_id filter count")(() => byUser.data.length === 2);
  TestValidator.equals("user_id filter id set")([
    recA.id, recC.id
  ].sort())(byUser.data.map(r => r.id).sort());

  // Step 3: Filter by product_id
  const byProduct = await api.functional.favoritesPersonalization.recommendations.patch(connection, {
    product_id: "22222222-2222-2222-2222-222222222222",
    limit: 10,
    offset: 0
  });
  typia.assert<IPageIRecommendation>(byProduct);
  TestValidator.predicate("product_id filter count")(() => byProduct.data.length === 2);
  TestValidator.equals("product_id filter id set")([
    recA.id, recB.id
  ].sort())(byProduct.data.map(r => r.id).sort());

  // Step 4: Filter by model_id
  const byModel = await api.functional.favoritesPersonalization.recommendations.patch(connection, {
    model_id: "model-x",
    limit: 10,
    offset: 0
  });
  typia.assert<IPageIRecommendation>(byModel);
  TestValidator.predicate("model_id filter count")(() => byModel.data.length === 2);
  TestValidator.equals("model_id filter id set")([
    recA.id, recC.id
  ].sort())(byModel.data.map(r => r.id).sort());

  // Step 5: Pagination - limit/offset
  const paged = await api.functional.favoritesPersonalization.recommendations.patch(connection, {
    limit: 2,
    offset: 0
  });
  typia.assert<IPageIRecommendation>(paged);
  TestValidator.predicate("pagination 1st page")(() => paged.data.length === 2);
  TestValidator.predicate("pagination meta current")(() => paged.pagination.current === 1);
  TestValidator.predicate("pagination meta limit")(() => paged.pagination.limit === 2);
  TestValidator.predicate("pagination meta records>=3")(() => paged.pagination.records >= 3);
  TestValidator.predicate("pagination meta pages>=2")(() => paged.pagination.pages >= 2);

  const paged2 = await api.functional.favoritesPersonalization.recommendations.patch(connection, {
    limit: 2,
    offset: 2
  });
  typia.assert<IPageIRecommendation>(paged2);
  TestValidator.predicate("pagination 2nd page")(() => paged2.data.length >= 1);

  // Step 6: Empty result when using non-matching filter
  const empty = await api.functional.favoritesPersonalization.recommendations.patch(connection, {
    user_id: "55555555-5555-5555-5555-555555555555",
    limit: 10,
    offset: 0
  });
  typia.assert<IPageIRecommendation>(empty);
  TestValidator.equals("empty user_id filter")([] as IRecommendation[])(empty.data);
}

