import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IPersonalizationAudit } from "@ORGANIZATION/PROJECT-api/lib/structures/IPersonalizationAudit";
import { IPersonalizationAudit as IPersonalizationAuditNS } from "@ORGANIZATION/PROJECT-api/lib/structures/IPersonalizationAudit";
import { IPageIPersonalizationAudit } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIPersonalizationAudit";
import typia from "typia";

export async function test_search_and_paginate_personalization_audits_with_filters(connection: api.IConnection): Promise<void> {
    // Step 1: Create two audit records with different users, action_types, timestamps
    const now = new Date();
    const earlier = new Date(now.getTime() - 60 * 1000); // 1 minute ago
    const user1 = "00000000-0000-0000-0000-000000000001";
    const user2 = "00000000-0000-0000-0000-000000000002";
    const evidenceHash1 = "evidencehash-1";
    const evidenceHash2 = "evidencehash-2";
    const actionType1 = "ADD_FAVORITE";
    const actionType2 = "REMOVE_BOOKMARK";

    const create1: IPersonalizationAuditNS.ICreate = {
        user_id: user1,
        favorite_id: null,
        bookmark_id: null,
        recommendation_id: null,
        personalization_event_id: null,
        action_type: actionType1,
        evidence_hash: evidenceHash1,
        timestamp: earlier.toISOString(),
    };
    const create2: IPersonalizationAuditNS.ICreate = {
        user_id: user2,
        favorite_id: null,
        bookmark_id: null,
        recommendation_id: null,
        personalization_event_id: null,
        action_type: actionType2,
        evidence_hash: evidenceHash2,
        timestamp: now.toISOString(),
    };

    const audit1 = await api.functional.favoritesPersonalization.personalizationAudits.post(connection, create1);
    const audit2 = await api.functional.favoritesPersonalization.personalizationAudits.post(connection, create2);
    typia.assert<IPersonalizationAudit>(audit1);
    typia.assert<IPersonalizationAudit>(audit2);

    // Step 2: Retrieve all audits (no filters)
    const allReq: IPersonalizationAuditNS.IRequest = {};
    const all = await api.functional.favoritesPersonalization.personalizationAudits.patch(connection, allReq);
    typia.assert<IPageIPersonalizationAudit>(all);
    TestValidator.predicate("should find at least two records")(() => all.data.length >= 2);
    // Audit1 and audit2 should be in results
    TestValidator.predicate("should include created audits")(() =>
        all.data.some((a) => a.id === audit1.id) && all.data.some((a) => a.id === audit2.id)
    );

    // Step 3: Search by user_id
    const user1Req: IPersonalizationAuditNS.IRequest = { user_id: user1 };
    const byUser1 = await api.functional.favoritesPersonalization.personalizationAudits.patch(connection, user1Req);
    typia.assert<IPageIPersonalizationAudit>(byUser1);
    TestValidator.predicate("all audits should belong to user1")(() =>
        byUser1.data.every((a) => a.user_id === user1)
    );

    // Step 4: Search by action_type
    const actType2Req: IPersonalizationAuditNS.IRequest = { action_type: actionType2 };
    const byActType2 = await api.functional.favoritesPersonalization.personalizationAudits.patch(connection, actType2Req);
    typia.assert<IPageIPersonalizationAudit>(byActType2);
    TestValidator.predicate("all audits should have action_type2")(() =>
        byActType2.data.every((a) => a.action_type === actionType2)
    );

    // Step 5: Sort descending by timestamp
    const sortReq: IPersonalizationAuditNS.IRequest = {
        limit: 10,
    };
    const sorted = await api.functional.favoritesPersonalization.personalizationAudits.patch(connection, sortReq);
    typia.assert<IPageIPersonalizationAudit>(sorted);
    const timestamps = sorted.data.map((a) => new Date(a.timestamp).getTime());
    TestValidator.predicate("should be sorted by descending timestamp")(() =>
        timestamps.every((t, i) => i === 0 || t <= timestamps[i - 1])
    );

    // Step 6: Filter by timestamp_from and timestamp_to
    const from = new Date(now.getTime() - 30 * 1000); // 30 seconds ago
    const timeFilteredReq: IPersonalizationAuditNS.IRequest = {
        timestamp_from: from.toISOString(),
        timestamp_to: now.toISOString(),
    };
    const byTime = await api.functional.favoritesPersonalization.personalizationAudits.patch(connection, timeFilteredReq);
    typia.assert<IPageIPersonalizationAudit>(byTime);
    TestValidator.predicate("all audits timestamps in window")(() =>
        byTime.data.every((a) => {
            const ts = new Date(a.timestamp).getTime();
            return ts >= from.getTime() && ts <= now.getTime();
        })
    );

    // Step 7: Paginate (limit=1, then offset=1)
    const pagedFirstReq: IPersonalizationAuditNS.IRequest = { limit: 1, offset: 0 };
    const pagedFirst = await api.functional.favoritesPersonalization.personalizationAudits.patch(connection, pagedFirstReq);
    typia.assert<IPageIPersonalizationAudit>(pagedFirst);
    TestValidator.predicate("limit=1 returns 1 row")(() => pagedFirst.data.length === 1);
    const pagedSecondReq: IPersonalizationAuditNS.IRequest = { limit: 1, offset: 1 };
    const pagedSecond = await api.functional.favoritesPersonalization.personalizationAudits.patch(connection, pagedSecondReq);
    typia.assert<IPageIPersonalizationAudit>(pagedSecond);
    TestValidator.predicate("limit=1, offset=1 returns 1 row")(() => pagedSecond.data.length === 1);
    TestValidator.predicate("paged records are different")(() =>
        pagedFirst.data[0].id !== pagedSecond.data[0].id
    );

    // Step 8: Filter that returns no records
    const noResultReq: IPersonalizationAuditNS.IRequest = { evidence_hash: "no-such-hash-123" };
    const noResult = await api.functional.favoritesPersonalization.personalizationAudits.patch(connection, noResultReq);
    typia.assert<IPageIPersonalizationAudit>(noResult);
    TestValidator.equals("empty dataset")([] as IPersonalizationAudit[])(noResult.data);

    // Step 9: All pagination metadata matches expectations
    TestValidator.predicate("pagination data validity")(() => {
        const p = all.pagination;
        return (
            typeof p.current === "number" &&
            typeof p.limit === "number" &&
            typeof p.records === "number" &&
            typeof p.pages === "number" &&
            p.records >= 2 &&
            p.pages >= 1
        );
    });
}

