import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IPersonalizationAudit } from "@ORGANIZATION/PROJECT-api/lib/structures/IPersonalizationAudit";
import typia from "typia";

export async function test_update_personalization_audit_by_id_and_invalid_update_attempts(
  connection: api.IConnection
): Promise<void> {
  // 1. Create a valid personalization audit record
  const createInput: IPersonalizationAudit.ICreate = {
    user_id: "11111111-1111-1111-1111-111111111111",
    favorite_id: null,
    bookmark_id: null,
    recommendation_id: null,
    personalization_event_id: null,
    action_type: "ACTION_CREATE",
    evidence_hash: "evidence1234567890abcdef",
    timestamp: new Date().toISOString(),
  };
  const created = await api.functional.favoritesPersonalization.personalizationAudits.post(
    connection,
    createInput
  );
  typia.assert<IPersonalizationAudit>(created);
  TestValidator.equals("user_id should match")(createInput.user_id)(created.user_id);
  TestValidator.equals("action_type should match")(createInput.action_type)(created.action_type);

  // 2. Update the audit record with valid changes
  const updateInput: IPersonalizationAudit.IUpdate = {
    action_type: "ACTION_UPDATE",
    evidence_hash: "updated-evidence-hash",
    timestamp: new Date().toISOString(),
  };
  const updated = await api.functional.favoritesPersonalization.personalizationAudits.put(
    connection,
    created.id,
    updateInput
  );
  typia.assert<IPersonalizationAudit>(updated);
  TestValidator.equals("update action_type")(updateInput.action_type)(updated.action_type);
  TestValidator.equals("update evidence_hash")(updateInput.evidence_hash)(updated.evidence_hash);
  TestValidator.equals("update timestamp")(updateInput.timestamp)(updated.timestamp);
  // original immutable fields stay the same
  TestValidator.equals("user_id is immutable")(created.user_id)(updated.user_id);

  // 3. Attempt update with invalid data: invalid type for action_type (number instead of string)
  await TestValidator.httpError("invalid type for action_type")(400)(async () => {
    await api.functional.favoritesPersonalization.personalizationAudits.put(
      connection,
      created.id,
      { ...updateInput, action_type: 123 as unknown as string }
    );
  });

  // 4. Attempt update with non-existent ID
  await TestValidator.httpError("non-existent ID")(404)(async () => {
    await api.functional.favoritesPersonalization.personalizationAudits.put(
      connection,
      "00000000-0000-0000-0000-000000000000",
      updateInput
    );
  });

  // 5. Attempt to mutate restricted/immutable fields (id, user_id)
  // These fields should not be settable and either ignored or error
  // Because IUpdate type does not allow these, we test by injecting extra props
  await TestValidator.httpError("attempt to mutate user_id")(400, 422)(async () => {
    // @ts-expect-error: Forced invalid
    await api.functional.favoritesPersonalization.personalizationAudits.put(
      connection,
      created.id,
      Object.assign({}, updateInput, { user_id: "different-user-id" })
    );
  });
  // 6. Optionally: Confirm audit trail not broken (updated record still conforms)
  const confirm = await api.functional.favoritesPersonalization.personalizationAudits.put(
    connection,
    created.id,
    {}
  );
  typia.assert<IPersonalizationAudit>(confirm);
  TestValidator.equals("id unchanged")(created.id)(confirm.id);
  TestValidator.equals("user_id unchanged")(created.user_id)(confirm.user_id);
}

