    const ownerB = "bbbbbbbb-bbbb-bbbb-bbbb-bbbbbbbbbbbb";

    const modelAInput: IAiModel.ICreate = {
        organization_id: orgId,
        owner_id: ownerA,
        name: "Model_Filter_Test_Alpha",
        model_type: "recommendation",
        description: "Test model - alpha",
    };
    const modelBInput: IAiModel.ICreate = {
        organization_id: orgId,
        owner_id: ownerB,
        name: "Model_Filter_Test_Beta",
        model_type: "fraud",
        description: "Test model - beta",
    };
    const modelA = await api.functional.advancedAI.aiModels.post(connection, modelAInput);
    typia.assert<IAiModel>(modelA);
    const modelB = await api.functional.advancedAI.aiModels.post(connection, modelBInput);
    typia.assert<IAiModel>(modelB);

    // 2. Basic list: all AI models (unfiltered, default pagination)
    const listAllReq = {};
    const pageAll = await api.functional.advancedAI.aiModels.patch(connection, listAllReq);
    typia.assert<IPageIAiModel>(pageAll);
    TestValidator.predicate("at least 2 models exist")(() => pageAll.data.length >= 2);
    TestValidator.predicate("pagination fields exist")(() => typeof pageAll.pagination.current === "number" && typeof pageAll.pagination.limit === "number");

    // 3. Filter by model_type
    const filterTypeReq = { model_type: "recommendation" };
    const pageType = await api.functional.advancedAI.aiModels.patch(connection, filterTypeReq);
    typia.assert<IPageIAiModel>(pageType);
    TestValidator.predicate("only model_type:recommendation returned")(() => pageType.data.every(d => d.model_type === "recommendation"));
    TestValidator.predicate("modelA in result, modelB not")(() => !!pageType.data.find(d => d.id === modelA.id) && !pageType.data.find(d => d.id === modelB.id));

    // 4. Filter by owner_id
    const filterOwnerReq = { owner_id: ownerB };
    const pageOwner = await api.functional.advancedAI.aiModels.patch(connection, filterOwnerReq);
    typia.assert<IPageIAiModel>(pageOwner);
    TestValidator.predicate("owner_id filter valid")(() => pageOwner.data.every(d => d.owner_id === ownerB));
    TestValidator.predicate("modelB in result, modelA not")(() => !!pageOwner.data.find(d => d.id === modelB.id) && !pageOwner.data.find(d => d.id === modelA.id));

    // 5. Ordering: Ensure order is by inserted sequence (no explicit sort param), just check both models present
    const ids = pageAll.data.map(d => d.id);
    TestValidator.predicate("both test models in 'all' list")(() => ids.includes(modelA.id) && ids.includes(modelB.id));

    // 6. Test empty result case (random filter)
    const emptyResReq = { model_type: "no_such_type_testcase" };
    const pageEmpty = await api.functional.advancedAI.aiModels.patch(connection, emptyResReq);
    typia.assert<IPageIAiModel>(pageEmpty);
    TestValidator.equals("empty data", () => false)([] as IAiModel[])(pageEmpty.data);
    // Should have correct pagination
    TestValidator.predicate("pagination fields valid (empty)")(() => typeof pageEmpty.pagination.current === "number");

    // 7. Pagination metadata correctness
    TestValidator.predicate("pagination current is 1")(() => pageAll.pagination.current === 1);
    TestValidator.predicate("pagination limit positive")(() => pageAll.pagination.limit > 0);
}

