export async function test_api_update_ai_model_metadata_with_valid_and_invalid_scenarios(connection: api.IConnection): Promise<void> {
    // Step 1: Create an AI model for update testing
    const createInput: IAiModel.ICreate = {
        organization_id: uuidv4(),
        owner_id: uuidv4(),
        name: `UnitTestModel_${Date.now()}`,
        model_type: "classification",
        description: "Initial AI model for update test."
    };

    const created: IAiModel = await api.functional.advancedAI.aiModels.post(connection, createInput);
    typia.assert<IAiModel>(created);
    TestValidator.equals("AI model creation - name")<string>(createInput.name)(created.name);
    TestValidator.equals("AI model creation - model_type")<string>(createInput.model_type)(created.model_type);

    // Step 2: Valid update
    const updateInput: IAiModel.IUpdate = {
        name: createInput.name + "_updated",
        description: "Updated model description.",
        model_type: "recommendation",
        owner_id: uuidv4(),
        organization_id: createInput.organization_id
    };
    const updated: IAiModel = await api.functional.advancedAI.aiModels.put(connection, created.id, updateInput);
    typia.assert<IAiModel>(updated);
    TestValidator.equals("AI model updated - id")<string>(created.id)(updated.id);
    TestValidator.equals("AI model updated - description")<string>(updateInput.description!)(updated.description);
    TestValidator.equals("AI model updated - name")<string>(updateInput.name!)(updated.name);
    TestValidator.equals("AI model updated - model_type")<string>(updateInput.model_type!)(updated.model_type);
    TestValidator.equals("AI model updated - owner_id")<string>(updateInput.owner_id!)(updated.owner_id);
    TestValidator.equals("AI model updated - organization_id")<string>(updateInput.organization_id!)(updated.organization_id);
    TestValidator.predicate("updated_at changes")(!!updated.updated_at && updated.updated_at !== created.updated_at);

    // Step 3-1: Invalid update - non-existent/random UUID as target ID
    const randomId = uuidv4();
    await TestValidator.httpError("Update with non-existent model id")(404)(
        () => api.functional.advancedAI.aiModels.put(connection, randomId, updateInput)
    );

    // Step 3-2: Invalid update - empty input object
    await TestValidator.httpError("Update with empty update input")(400, 422)(
        () => api.functional.advancedAI.aiModels.put(connection, created.id, {})
    );

    // Step 3-3: Invalid update - invalid types in payload
    const invalidUpdateInput: IAiModel.IUpdate = {
        name: 123 as any,
        model_type: true as any,
        owner_id: false as any,
        organization_id: 55 as any,
        description: null as any
    };
    await TestValidator.httpError("Update with invalid types in update input")(400, 422)(
        () => api.functional.advancedAI.aiModels.put(connection, created.id, invalidUpdateInput)
    );
}

