import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IAiModelVersion } from "@ORGANIZATION/PROJECT-api/lib/structures/IAiModelVersion";
import typia from "typia";

export async function test_list_and_search_ai_model_versions_with_pagination_and_filters(connection: api.IConnection): Promise<void> {
    // 1. Setup - create multiple model versions
    const ai_model_id1 = "aaaaaaaa-aaaa-aaaa-aaaa-aaaaaaaaaaaa";
    const ai_model_id2 = "bbbbbbbb-bbbb-bbbb-bbbb-bbbbbbbbbbbb";
    const now = new Date();

    const v1 = await api.functional.advancedAI.aiModelVersions.post(connection, {
        ai_model_id: ai_model_id1,
        version: "v1.0.0",
        version_hash: "hash1",
        artifact_url: "https://bucket/model/v1.0.0",
        framework: "TensorFlow",
        metadata: "{\"acc\":0.99}"
    });
    const v2 = await api.functional.advancedAI.aiModelVersions.post(connection, {
        ai_model_id: ai_model_id1,
        version: "v1.0.1",
        version_hash: "hash2",
        artifact_url: "https://bucket/model/v1.0.1",
        framework: "TensorFlow",
        metadata: "{\"acc\":0.991}"
    });
    const v3 = await api.functional.advancedAI.aiModelVersions.post(connection, {
        ai_model_id: ai_model_id2,
        version: "v2.0.0",
        version_hash: "hash3",
        artifact_url: "https://bucket/model/v2.0.0",
        framework: "PyTorch",
        metadata: "{\"acc\":0.98}"
    });

    const createdVersions: IAiModelVersion[] = [v1, v2, v3];
    createdVersions.forEach(ver => typia.assert<IAiModelVersion>(ver));

    // 2. List all
    let page = await api.functional.advancedAI.aiModelVersions.patch(connection, {});
    typia.assert(page);
    TestValidator.predicate("created records in all record fetch")(() =>
        createdVersions.every(cv => page.data.some(d => d.id === cv.id))
    );
    TestValidator.predicate("records must match IAiModelVersion")(() =>
        page.data.every(rv => typia.is<IAiModelVersion>(rv))
    );

    // 3. Pagination (limit 2, page 1)
    page = await api.functional.advancedAI.aiModelVersions.patch(connection, { limit: 2, current: 1 });
    typia.assert(page);
    TestValidator.equals("Pagination page 1 count 2")([0,1])(page.data.map((_,i)=>i));
    TestValidator.equals("pagination.limit")(2)(page.pagination.limit);

    // 4. Filter - By ai_model_id
    page = await api.functional.advancedAI.aiModelVersions.patch(connection, { ai_model_id: ai_model_id1 });
    typia.assert(page);
    TestValidator.predicate("Filter by ai_model_id1 contains only ai_model_id1 records")(() =>
        page.data.every(v => v.ai_model_id === ai_model_id1)
    );
    TestValidator.predicate("ai_model_id1: contains v1 and v2")(() =>
        [v1.id, v2.id].every(id => page.data.some(x => x.id === id)) && page.data.every(x => [v1.id, v2.id].includes(x.id))
    );

    // 5. Filter - By version (partial match)
    page = await api.functional.advancedAI.aiModelVersions.patch(connection, { version: "v1" });
    typia.assert(page);
    TestValidator.predicate("Partial version filter: only v1 versions")(() =>
        page.data.every(v => v.version.startsWith("v1"))
    );
    TestValidator.predicate("Partial version filter: returns v1, v2")(() =>
        [v1.id, v2.id].every(id => page.data.some(x => x.id === id))
    );

    // 6. Filter - By framework
    page = await api.functional.advancedAI.aiModelVersions.patch(connection, { framework: "PyTorch" });
    typia.assert(page);
    TestValidator.predicate("Framework=PyTorch returns only v3")(() => page.data.length === 1 && page.data[0].id === v3.id);

    // 7. Filter - By deployed (should test both true/false)
    // There is no way to set deployed state via POST, so this just checks API does not error and returns something.
    // (In real test may patch deployment, here just test response shape)
    page = await api.functional.advancedAI.aiModelVersions.patch(connection, { deployed: false });
    typia.assert(page);
    TestValidator.predicate("Deployed filter false does not error")(() => Array.isArray(page.data));

    // 8. Filter - By creation date (use created_from, window from now-1min)
    const minAgo = new Date(now.getTime() - 60*1000).toISOString();
    const maxInFuture = new Date(now.getTime() + 60*1000).toISOString();
    page = await api.functional.advancedAI.aiModelVersions.patch(connection, { created_from: minAgo, created_to: maxInFuture });
    typia.assert(page);
    TestValidator.predicate("Created_at between window includes all created")(() =>
        createdVersions.every(cv => page.data.some(d => d.id === cv.id))
    );

    // 9. Negative: filter should yield empty
    page = await api.functional.advancedAI.aiModelVersions.patch(connection, { ai_model_id: "cccccccc-cccc-cccc-cccc-cccccccccccc" });
    typia.assert(page);
    TestValidator.equals("Non-existent model id yields empty")([] as IAiModelVersion[])(page.data);
}

