export async function test_api_update_ai_model_version_with_valid_and_invalid_conditions(connection: api.IConnection): Promise<void> {
  // 1. Create prerequisite model version (simulate a parent model UUID, will not check parent existence)
  const parentModelId = "c0ffee00-cafe-babe-beef-aaaaaaaaaaaa"; // Demo UUID for testing only
  const createInput: IAiModelVersion.ICreate = {
    ai_model_id: parentModelId,
    version: "v1.0.0",
    version_hash: "HASH_v1.0.0",
    artifact_url: "s3://bucket/model/v1.0.0",
    framework: "PyTorch",
    metadata: "{\"acc\":0.95}"
  };

  const created = await api.functional.advancedAI.aiModelVersions.post(connection, createInput);
  typia.assert<IAiModelVersion>(created);

  // 2. Happy path update
  const updatePayload: IAiModelVersion.IUpdate = {
    version: "v1.0.1",
    version_hash: "HASH_v1.0.1",
    artifact_url: "s3://bucket/model/v1.0.1",
    framework: "PyTorch",
    metadata: "{\"acc\":0.96}"
  };
  const updated = await api.functional.advancedAI.aiModelVersions.put(connection, created.id, updatePayload);
