  TestValidator.equals("update: ai_model_id")<string>(created.ai_model_id)(updated.ai_model_id);
  TestValidator.equals("update: version")<string>(updatePayload.version!)(updated.version);
  TestValidator.equals("update: version_hash")<string>(updatePayload.version_hash!)(updated.version_hash);
  TestValidator.equals("update: artifact_url")<string>(updatePayload.artifact_url!)(updated.artifact_url);
  TestValidator.equals("update: framework")<string>(updatePayload.framework!)(updated.framework);
  TestValidator.equals("update: metadata")<string>(updatePayload.metadata!)(updated.metadata);

