export async function test_api_delete_ai_model_version_and_handle_edge_conditions(connection: api.IConnection): Promise<void> {
    // 1. Prepare minimal valid model version payload
    const modelVersionInput: IAiModelVersion.ICreate = {
        ai_model_id: uuidv4(),
        version: `v${Math.floor(Math.random()*1000)}.0.0`,
        version_hash: uuidv4(),
        artifact_url: `s3://test-bucket/model-artifact-${Date.now()}`,
        framework: "PyTorch",
        metadata: "{\"purpose\":\"test deletion\"}"
    };

    // 2. Create the model version (POST)
    const created: IAiModelVersion = await api.functional.advancedAI.aiModelVersions.post(connection, modelVersionInput);
    typia.assert<IAiModelVersion>(created);
    TestValidator.equals("created version matches input", (k) => k==="id" || k==="created_at" || k==="deployed_at")(modelVersionInput)({
        ai_model_id: created.ai_model_id,
        version: created.version,
        version_hash: created.version_hash,
        artifact_url: created.artifact_url,
        framework: created.framework,
        metadata: created.metadata,
    });

    // 3. Delete the newly created model version (DELETE)
    const deleted = await api.functional.advancedAI.aiModelVersions.delete(connection, created.id);
    typia.assert<{id: string, status: string, deleted_at?: string}>(deleted);
