    // 5. Attempt to delete a completely random non-existent id
    const randomNonExistentId = uuidv4();
    await TestValidator.httpError("deleting a non-existent ai model version should fail")(404, 400, 410)(
        () => api.functional.advancedAI.aiModelVersions.delete(connection, randomNonExistentId)
    );
