import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IAITrainingData } from "@ORGANIZATION/PROJECT-api/lib/structures/IAITrainingData";
import { IAiTrainingData } from "@ORGANIZATION/PROJECT-api/lib/structures/IAiTrainingData";
import { IPageIAiTrainingData } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIAiTrainingData";
import typia from "typia";

export async function test_search_ai_training_data_with_pagination_and_various_filters(connection: api.IConnection): Promise<void> {
  // Prereq: Create multiple varying AI training datasets
  const ai_model_id_1 = "c8e856ee-1e49-4fde-8083-1e2609178f11";
  const ai_model_id_2 = "8e7f0de2-b54c-4193-91a7-b79c3668179d";
  // Unique names, versions, uri, schemas
  const datasets: IAITrainingData.ICreate[] = [
    {
      ai_model_id: ai_model_id_1,
      name: "2025-Q2-Orders",
      version: "v1.0",
      source_uri: "s3://bucket/aidata/orders-2025Q2-v1.parquet",
      schema_hash: "HASHQ2AABB11",
      record_count: 10000,
    },
    {
      ai_model_id: ai_model_id_2,
      name: "UserActivity-2025",
      version: "v2.1",
      source_uri: "s3://bucket/aidata/activity-2025-v2.parquet",
      schema_hash: "HASHACT2B12",
      record_count: 2000,
    },
    {
      ai_model_id: null,
      name: "ExternalData-Shipments",
      version: "202406-ext",
      source_uri: "gs://extdata/shipments-2024.parquet",
      schema_hash: "HASHSHIP99",
      record_count: null,
    },
  ];
  const created: IAiTrainingData[] = [];
  for (const input of datasets) {
    const data = await api.functional.advancedAI.aiTrainingData.post(connection, input);
    typia.assert<IAiTrainingData>(data);
    created.push(data);
  }

  // 1. Fetch all, default pagination
  const allRes = await api.functional.advancedAI.aiTrainingData.patch(connection, {});
  typia.assert<IPageIAiTrainingData>(allRes);
  TestValidator.predicate("got at least 3 datasets")(() => allRes.data.length >= 3);
  TestValidator.equals("pagination correctness")(
    allRes.pagination.limit,
  )(100);

  // 2. Pagination: limit < total, test page boundaries
  const page1 = await api.functional.advancedAI.aiTrainingData.patch(connection, { limit: 2, page: 1 });
  const page2 = await api.functional.advancedAI.aiTrainingData.patch(connection, { limit: 2, page: 2 });
  typia.assert<IPageIAiTrainingData>(page1);
  typia.assert<IPageIAiTrainingData>(page2);
  TestValidator.equals("page 1 count")(2)(page1.data.length);
  TestValidator.predicate("page 2 returns at least 1")(() => page2.data.length >= 1);
  TestValidator.equals("page boundary continuity")(
    allRes.data
      .slice(0, 2)
      .map(d => d.id),
  )(page1.data.map(d => d.id));

  // 3. Search by exact name
  const exactByName = await api.functional.advancedAI.aiTrainingData.patch(connection, { name: "UserActivity-2025" });
  typia.assert<IPageIAiTrainingData>(exactByName);
  TestValidator.equals("UserActivity by exact name should be one")(
    exactByName.data.length,
  )(1);
  TestValidator.equals("name match")(
    exactByName.data[0].name,
  )("UserActivity-2025");

  // 4. Search by partial name (simulate, expecting at least 1, implementation may vary)
  const partial = await api.functional.advancedAI.aiTrainingData.patch(connection, { name: "2025" });
  typia.assert<IPageIAiTrainingData>(partial);
  TestValidator.predicate("at least 2 datasets with '2025' in name")(
    () => partial.data.filter(x => x.name.includes("2025")).length >= 2
  );

  // 5. Filter by ai_model_id
  const byModel1 = await api.functional.advancedAI.aiTrainingData.patch(connection, { ai_model_id: ai_model_id_1 });
  typia.assert<IPageIAiTrainingData>(byModel1);
  TestValidator.predicate("model1 datasets only")(
    () => byModel1.data.every(x => x.ai_model_id === ai_model_id_1)
  );

  const byModel2 = await api.functional.advancedAI.aiTrainingData.patch(connection, { ai_model_id: ai_model_id_2 });
  typia.assert<IPageIAiTrainingData>(byModel2);
  TestValidator.predicate("model2 datasets only")(
    () => byModel2.data.every(x => x.ai_model_id === ai_model_id_2)
  );

  const byNullModel = await api.functional.advancedAI.aiTrainingData.patch(connection, { ai_model_id: null });
  typia.assert<IPageIAiTrainingData>(byNullModel);
  TestValidator.predicate("datasets without model id")(
    () => byNullModel.data.every(x => x.ai_model_id === null)
  );

  // 6. Filter by version
  const byVersion = await api.functional.advancedAI.aiTrainingData.patch(connection, { version: "202406-ext" });
  typia.assert<IPageIAiTrainingData>(byVersion);
  TestValidator.equals("version match, one data")(
    byVersion.data.length,
  )(1);
  TestValidator.equals("found version exact")(
    byVersion.data[0].version,
  )("202406-ext");

  // 7. Schema hash, exact
  const bySchemaHash = await api.functional.advancedAI.aiTrainingData.patch(connection, { schema_hash: "HASHACT2B12" });
  typia.assert<IPageIAiTrainingData>(bySchemaHash);
  TestValidator.equals("one found by schema_hash")(
    bySchemaHash.data.length,
  )(1);
  TestValidator.equals("schema hash matches")(
    bySchemaHash.data[0].schema_hash,
  )("HASHACT2B12");

  // 8. Record count: min/max boundaries and edge/negative case
  const rcMin = await api.functional.advancedAI.aiTrainingData.patch(connection, { record_count_from: 5000 });
  typia.assert<IPageIAiTrainingData>(rcMin);
  TestValidator.predicate("only record_count >= 5000")(
    () => rcMin.data.every(x => x.record_count !== null && x.record_count >= 5000)
  );

  const rcMax = await api.functional.advancedAI.aiTrainingData.patch(connection, { record_count_to: 5000 });
  typia.assert<IPageIAiTrainingData>(rcMax);
  TestValidator.predicate("only record_count <= 5000")(
    () => rcMax.data.every(x => x.record_count !== null && x.record_count <= 5000)
  );

  // 9. Negative - name impossible
  const noRes = await api.functional.advancedAI.aiTrainingData.patch(connection, { name: "ShouldNotExistName-XYZ-" });
  typia.assert<IPageIAiTrainingData>(noRes);
  TestValidator.equals("no result for impossible name")(
    noRes.data.length,
  )(0);

  // 10. Negative - impossible record count (huge)
  const noBigRes = await api.functional.advancedAI.aiTrainingData.patch(connection, { record_count_from: 99999999 });
  typia.assert<IPageIAiTrainingData>(noBigRes);
  TestValidator.equals("no result for too large record_count")(
    noBigRes.data.length,
  )(0);

  // 11. Every result should validate IAiTrainingData shape
  for (const dto of [allRes, page1, page2, exactByName, partial, byModel1, byModel2, byNullModel, byVersion, bySchemaHash, rcMin, rcMax]) {
    for (const item of dto.data) {
      typia.assert<IAiTrainingData>(item);
    }
  }
}

