export async function test_api_policy_list_permission_policies_with_filter_and_pagination(connection: api.IConnection): Promise<void> {
  // 1. Standard pagination, expect normal result
  const standardReq = { page: 1, limit: 2 };
  const standardRes = await api.functional.securityCompliance.policy.patch(connection, standardReq);
  typia.assert<IPageIPolicy>(standardRes);
  TestValidator.equals("pagination.page")(standardReq.page ?? 1)(standardRes.pagination.current);
  TestValidator.equals("pagination.limit")(standardReq.limit ?? 10)(standardRes.pagination.limit);

  // 2. Name filter (assuming at least one exists)
  let examplePolicyName: string | undefined = undefined;
  if (standardRes.data.length > 0) examplePolicyName = standardRes.data[0].name;
  if (examplePolicyName) {
    const filterReq = { name: examplePolicyName, page: 1, limit: 10 };
    const filterRes = await api.functional.securityCompliance.policy.patch(connection, filterReq);
    typia.assert<IPageIPolicy>(filterRes);
    TestValidator.predicate("at least one result")(() => filterRes.data.length > 0);
    // All returned entries must match filter
    filterRes.data.forEach(item => TestValidator.equals("filter by name")(examplePolicyName!)(item.name));
  }

  // 3. Condition filter (syntactic validation only)
  if (standardRes.data.length > 0) {
    const exCond = standardRes.data[0].conditions;
    const condReq = { conditions: exCond, page: 1, limit: 5 };
    const condRes = await api.functional.securityCompliance.policy.patch(connection, condReq);
    typia.assert<IPageIPolicy>(condRes);
    TestValidator.predicate("at least one result by conditions")(() => condRes.data.length > 0);
    // Not strictly checking string-equality - just non-empty result
  }

  // 4. Filtering to an empty set
  const emptyReq = { name: "___NO_SUCH_POLICY___", page: 1, limit: 3 };
  const emptyRes = await api.functional.securityCompliance.policy.patch(connection, emptyReq);
  typia.assert<IPageIPolicy>(emptyRes);
  TestValidator.equals("empty result - data[]")([] as IPolicy[])(emptyRes.data);

  // 5. Invalid filter parameter (impossible page number), expect error
  await TestValidator.httpError("invalid page number")(400)(async () => {
    const badReq = { page: -1, limit: 3 };
    return api.functional.securityCompliance.policy.patch(connection, badReq);
  });

  // 6. Invalid sort_direction type, expect validation error (should be 'asc' or 'desc')
  await TestValidator.httpError("bad sort_direction")(400)(async () => {
    const badSortReq = { sort_direction: "invalid", page: 1, limit: 1 } as any;
    return api.functional.securityCompliance.policy.patch(connection, badSortReq);
  });
}

