import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IAuditLog } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuditLog";
import typia from "typia";

export async function test_update_audit_log_with_field_restrictions_and_error_handling(connection: api.IConnection): Promise<void> {
    // 1. Create a valid audit log entry (prepare needed data for required fields)
    const createBody: IAuditLog.ICreate = {
        action: "create",
        entity_type: "user",
        context: JSON.stringify({ before: null, after: { x: 1 } }),
        user_id: "11111111-1111-1111-1111-111111111111", // dummy ID
        entity_id: "22222222-2222-2222-2222-222222222222",
        evidence_archive_id: "33333333-3333-3333-3333-333333333333",
    };
    const created = await api.functional.securityCompliance.auditLog.post(connection, createBody);
    typia.assert<IAuditLog>(created);

    // 2. Update the audit log entry by ID with permissible fields
    const updateBody = {
        evidence_archive_id: "44444444-4444-4444-4444-444444444444",
        context: JSON.stringify({ before: null, after: { x: 2 }, changed: true }),
    };
    const updated = await api.functional.securityCompliance.auditLog.put(connection, created.id, updateBody);
    typia.assert<IAuditLog>(updated);

    // confirm allowed fields changed
    TestValidator.equals("evidence_archive_id")(updateBody.evidence_archive_id!)(updated.evidence_archive_id);
    TestValidator.equals("context")(updateBody.context!)(updated.context);
    // confirm immutable fields unchanged
    TestValidator.equals("action")(created.action)(updated.action);
    TestValidator.equals("entity_type")(created.entity_type)(updated.entity_type);
    TestValidator.equals("entity_id")(created.entity_id)(updated.entity_id);
    TestValidator.equals("user_id")(created.user_id)(updated.user_id);

    // 3. Attempt to update a non-existent audit log ID (expect 404)
    await TestValidator.httpError("audit log update not found error")(404)(() =>
        api.functional.securityCompliance.auditLog.put(connection, "aaaaaaaa-aaaa-aaaa-aaaa-aaaaaaaaaaaa", updateBody)
    );

    // 4. Attempt invalid payload: send malformed context (expect validation error, 400 or 422)
    const invalidContextUpdate = { context: "not a json string" };
    await TestValidator.httpError("invalid context field")(400, 422)(() =>
        api.functional.securityCompliance.auditLog.put(connection, created.id, invalidContextUpdate)
    );

    // 5. Attempt to update an immutable field: 'action' (should NOT change and/or return error)
    const illegalUpdate = { action: "delete" } as any;
    await TestValidator.httpError("update immutable field: action")(400, 422, 403)(() =>
        api.functional.securityCompliance.auditLog.put(connection, created.id, illegalUpdate)
    );
}

