import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IComplianceReport } from "@ORGANIZATION/PROJECT-api/lib/structures/IComplianceReport";
import { IPage } from "@ORGANIZATION/PROJECT-api/lib/structures/IPage";
import typia from "typia";

export async function test_api_list_compliance_reports_with_filtering_and_pagination(
  connection: api.IConnection
): Promise<void> {
  // 1. Valid filter and pagination: wide query (should get results if any data exists)
  const request: IComplianceReport.IRequest = { report_type: undefined, status: undefined };
  const wideResult = await api.functional.securityCompliance.complianceReport.patch(connection, request);
  typia.assert<IPage<IComplianceReport.ISummary>>(wideResult);
  TestValidator.predicate("pagination object present")(() => typeof wideResult.pagination === "object");
  TestValidator.predicate("data array present")(() => Array.isArray(wideResult.data));
  // if there is data, fields must match the filter (which is wide open)
  if (wideResult.data.length > 0) {
    for (const summary of wideResult.data) {
      typia.assert<IComplianceReport.ISummary>(summary);
    }
  }

  // 2. Filtered query: status & type (should get only filtered reports)
  const filteredRequest: IComplianceReport.IRequest = {
    report_type: "GDPR_EXPORT",
    status: "published"
  };
  const filteredResult = await api.functional.securityCompliance.complianceReport.patch(connection, filteredRequest);
  typia.assert<IPage<IComplianceReport.ISummary>>(filteredResult);
  for (const summary of filteredResult.data) {
    TestValidator.equals("report_type filter")(filteredRequest.report_type)(summary.report_type);
    TestValidator.equals("status filter")(filteredRequest.status)(summary.status);
  }

  // 3. Pagination: request limit 2 and various pages
  const paginationRequest: IComplianceReport.IRequest = {
    report_type: undefined,
    status: undefined
  };
  const pagedFirst = await api.functional.securityCompliance.complianceReport.patch(connection, { ...paginationRequest, limit: 2, current: 1 });
  typia.assert<IPage<IComplianceReport.ISummary>>(pagedFirst);
  TestValidator.equals("limit count")(2)(pagedFirst.pagination.limit);
  TestValidator.equals("current page")(1)(pagedFirst.pagination.current);
  if (pagedFirst.data.length > 0) {
    for (const summary of pagedFirst.data) {
      typia.assert<IComplianceReport.ISummary>(summary);
    }
  }

  // If there are multiple pages, check next page is different
  if ((pagedFirst.pagination.pages || 0) > 1) {
    const pagedSecond = await api.functional.securityCompliance.complianceReport.patch(connection, { ...paginationRequest, limit: 2, current: 2 });
    typia.assert<IPage<IComplianceReport.ISummary>>(pagedSecond);
    // Ensure that at least the "id" field is not the exact same in all data items
    const idsFirst = pagedFirst.data.map(x => x.id).sort();
    const idsSecond = pagedSecond.data.map(x => x.id).sort();
    TestValidator.predicate("paged data ids should differ between pages")(() => idsFirst.join() !== idsSecond.join());
  }

  // 4. Zero-result case: bogus status/type
  const zeroRequest: IComplianceReport.IRequest = {
    report_type: "NON_EXISTENT_TYPE",
    status: "bogus_status"
  };
  const zeroResult = await api.functional.securityCompliance.complianceReport.patch(connection, zeroRequest);
  typia.assert<IPage<IComplianceReport.ISummary>>(zeroResult);
  TestValidator.equals("empty result array")([] as IComplianceReport.ISummary[])(zeroResult.data);

  // 5. Invalid filter: malformed date
  const invalidDateRequest: IComplianceReport.IRequest = {
    generated_from: "not-a-date"
  } as any;
  await TestValidator.httpError("invalid generated_from date")(400)(() => api.functional.securityCompliance.complianceReport.patch(connection, invalidDateRequest));
}

