export async function test_list_data_retention_policies_with_filtering_and_edge_cases(connection: api.IConnection): Promise<void> {
    // 1. Default list, no filters
    const defaultReq: IDataRetention.IRequest = {};
    const defaultRes = await api.functional.securityCompliance.dataRetention.patch(connection, defaultReq);
    typia.assert<IPage<IDataRetention.ISummary>>(defaultRes);
    TestValidator.predicate("default list returns array")(() => Array.isArray(defaultRes.data));

    // 2. Pagination
    const paginatedReq: IDataRetention.IRequest = {};
    (paginatedReq as any).limit = 1;
    (paginatedReq as any).page = 1;
    const paginatedRes = await api.functional.securityCompliance.dataRetention.patch(connection, paginatedReq);
    typia.assert<IPage<IDataRetention.ISummary>>(paginatedRes);
    TestValidator.equals("pagination limit 1")<number>(1)(paginatedRes.pagination.limit);
    TestValidator.predicate("pagination page 1")(() => paginatedRes.pagination.current === 1);

    // 3. Filtering: (for test, use existing record property values if present)
    if (defaultRes.data.length > 0) {
        const [sample] = defaultRes.data;
        // By organization_id
        if (sample.organization_id) {
            const orgReq: IDataRetention.IRequest = { organization_id: sample.organization_id };
            const orgRes = await api.functional.securityCompliance.dataRetention.patch(connection, orgReq);
            typia.assert<IPage<IDataRetention.ISummary>>(orgRes);
            for(const rec of orgRes.data) {
              TestValidator.equals("filter by org")(sample.organization_id)(rec.organization_id);
            }
        }
        // By entity_type
        const entReq: IDataRetention.IRequest = { entity_type: sample.entity_type };
        const entRes = await api.functional.securityCompliance.dataRetention.patch(connection, entReq);
        typia.assert<IPage<IDataRetention.ISummary>>(entRes);
        for(const rec of entRes.data) {
            TestValidator.equals("filter by entity_type")(sample.entity_type)(rec.entity_type);
        }
        // By is_legal_hold
        const lholdReq: IDataRetention.IRequest = { is_legal_hold: sample.is_legal_hold };
        const lholdRes = await api.functional.securityCompliance.dataRetention.patch(connection, lholdReq);
        typia.assert<IPage<IDataRetention.ISummary>>(lholdRes);
        for(const rec of lholdRes.data) {
            TestValidator.equals("filter by legal_hold")(sample.is_legal_hold)(rec.is_legal_hold);
        }
        // Combination
        const comboReq: IDataRetention.IRequest = {
          organization_id: sample.organization_id || undefined,
          entity_type: sample.entity_type,
          is_legal_hold: sample.is_legal_hold
        };
        const comboRes = await api.functional.securityCompliance.dataRetention.patch(connection, comboReq);
        typia.assert<IPage<IDataRetention.ISummary>>(comboRes);
        for(const rec of comboRes.data) {
            if (sample.organization_id)
                TestValidator.equals("combo org")(sample.organization_id)(rec.organization_id);
            TestValidator.equals("combo type")(sample.entity_type)(rec.entity_type);
            TestValidator.equals("combo lh")(sample.is_legal_hold)(rec.is_legal_hold);
        }
    }
    // 4. Edge case: non-existing organization_id
    const fakeOrgReq: IDataRetention.IRequest = { organization_id: "00000000-0000-0000-0000-000000000000" };
    const fakeOrgRes = await api.functional.securityCompliance.dataRetention.patch(connection, fakeOrgReq);
    typia.assert<IPage<IDataRetention.ISummary>>(fakeOrgRes);
    TestValidator.equals("empty org_id filter result")(emptyArr<IDataRetention.ISummary>())(fakeOrgRes.data);
    // 5. Edge case: no matching entity_type
    const fakeEntReq: IDataRetention.IRequest = { entity_type: "_no_such_entity_type_" };
    const fakeEntRes = await api.functional.securityCompliance.dataRetention.patch(connection, fakeEntReq);
    typia.assert<IPage<IDataRetention.ISummary>>(fakeEntRes);
