import typia from "typia";

export async function test_api_evidence_archive_list_with_filter_and_pagination(connection: api.IConnection): Promise<void> {
  // 1. Basic listing with empty filter (should return paginated data)
  const req1: IEvidenceArchive.IRequest = {
    page: 1,
    limit: 5,
  };
  const resp1 = await api.functional.securityCompliance.evidenceArchive.patch(connection, req1);
  typia.assert<IPageIEvidenceArchive>(resp1);
  TestValidator.equals("pagination.page")(1)(resp1.pagination.current);
  TestValidator.equals("pagination.limit")(5)(resp1.pagination.limit);
  TestValidator.predicate("data array")(Array.isArray(resp1.data));

  // 2. Filtering by non-existent evidence_type (should return empty data)
  const req2: IEvidenceArchive.IRequest = {
    page: 1,
    limit: 5,
    evidence_type: "DOES_NOT_EXIST_TYPE_9999",
  };
  const resp2 = await api.functional.securityCompliance.evidenceArchive.patch(connection, req2);
  typia.assert<IPageIEvidenceArchive>(resp2);
  TestValidator.equals("empty data")([] as IEvidenceArchive[])(resp2.data);

  // 3. Filtering by evidence_type if possible, otherwise accept empty
  let filterEvidenceType = undefined;
  if (resp1.data.length > 0) {
    filterEvidenceType = resp1.data[0].evidence_type;
  }
  const req3: IEvidenceArchive.IRequest = {
    page: 1,
    limit: 3,
    evidence_type: filterEvidenceType,
  };
  const resp3 = await api.functional.securityCompliance.evidenceArchive.patch(connection, req3);
  typia.assert<IPageIEvidenceArchive>(resp3);
  if (filterEvidenceType) {
    for (const item of resp3.data) {
      TestValidator.equals("evidence_type filter")(filterEvidenceType)(item.evidence_type);
    }
  }

  // 4. Pagination test: request two successive pages, ensure items are different
  const req4a: IEvidenceArchive.IRequest = {
    page: 1,
    limit: 2
  };
  const req4b: IEvidenceArchive.IRequest = {
    page: 2,
    limit: 2
  };
  const resp4a = await api.functional.securityCompliance.evidenceArchive.patch(connection, req4a);
  const resp4b = await api.functional.securityCompliance.evidenceArchive.patch(connection, req4b);
  typia.assert<IPageIEvidenceArchive>(resp4a);
  typia.assert<IPageIEvidenceArchive>(resp4b);
  const page1Ids = resp4a.data.map((x) => x.id);
  const page2Ids = resp4b.data.map((x) => x.id);
  TestValidator.predicate("pagination does not repeat data")(() => {
    return !page1Ids.some((id) => page2Ids.includes(id));
  });

  // 5. Filtering with multiple fields: use filters from first item if possible
  let checksum = undefined;
  let created_at = undefined;
  if (resp1.data.length > 0) {
    checksum = resp1.data[0].checksum;
    created_at = resp1.data[0].created_at;
  }
  const req5: IEvidenceArchive.IRequest = {
    page: 1,
    limit: 3,
    evidence_type: filterEvidenceType,
    checksum,
    created_at_from: created_at,
    created_at_to: created_at
  };
  const resp5 = await api.functional.securityCompliance.evidenceArchive.patch(connection, req5);
  typia.assert<IPageIEvidenceArchive>(resp5);
  if (filterEvidenceType && checksum && created_at) {
    for (const item of resp5.data) {
      TestValidator.equals("multi field evidence_type")(filterEvidenceType)(item.evidence_type);
      TestValidator.equals("multi field checksum")(checksum)(item.checksum);
      TestValidator.equals("multi field created_at")(created_at)(item.created_at);
    }
  }

  // 6. Malformed request (bad evidence_type type)
  const badReq: any = {
    page: 1,
    limit: 1,
    evidence_type: 777 as any, // should be string
  };
  await TestValidator.error("bad request schema should error")(async () => {
    await api.functional.securityCompliance.evidenceArchive.patch(connection, badReq);
  });
}

