import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IEvidenceArchive } from "@ORGANIZATION/PROJECT-api/lib/structures/IEvidenceArchive";
import typia from "typia";

export async function test_api_create_evidence_archive_with_various_inputs(connection: api.IConnection): Promise<void> {
    // 1. Prepare fully valid payload
    const validInput: IEvidenceArchive.ICreate = {
        evidence_type: "PAYMENT_EXPORT",
        blob_url: "https://evidence-store.example.com/evidence1.pdf",
        size_bytes: 123456,
        checksum: "sha256:1234567890abcdef1234567890abcdef1234567890abcdef1234567890abcdef"
    };

    // 2. Execute POST with valid data
    const created = await api.functional.securityCompliance.evidenceArchive.post(connection, validInput);
    typia.assert<IEvidenceArchive>(created);
    TestValidator.equals("Created evidence matches input")(validInput.evidence_type)(created.evidence_type);
    TestValidator.equals("Created evidence matches blob_url")(validInput.blob_url)(created.blob_url);
    TestValidator.equals("Created evidence matches size")(validInput.size_bytes)(created.size_bytes);
    TestValidator.equals("Created evidence matches checksum")(validInput.checksum)(created.checksum);
    // Check immutable fields exist
    TestValidator.predicate("Evidence archive has id")(!!created.id);
    TestValidator.predicate("Evidence archive has created_at")(!!created.created_at);

    // 3. Missing required field: omit blob_url
    const missingBlobUrl = { ...validInput };
    delete (missingBlobUrl as any).blob_url;
    await TestValidator.httpError("Missing blob_url triggers error")(400)(() =>
        api.functional.securityCompliance.evidenceArchive.post(connection, missingBlobUrl as IEvidenceArchive.ICreate)
    );

    // 4. Invalid field: negative size_bytes
    const invalidSize: IEvidenceArchive.ICreate = { ...validInput, size_bytes: -1 };
    await TestValidator.httpError("Negative size_bytes triggers error")(400)(() =>
        api.functional.securityCompliance.evidenceArchive.post(connection, invalidSize)
    );

    // 5. Invalid field: empty evidence_type
    const emptyType: IEvidenceArchive.ICreate = { ...validInput, evidence_type: "" };
    await TestValidator.httpError("Empty evidence_type triggers error")(400)(() =>
        api.functional.securityCompliance.evidenceArchive.post(connection, emptyType)
    );

    // 6. Duplicate evidence (exact same checksum and blob_url)
    // Depending on system policy, this may trigger an error or allow duplicate.
    // We'll assert that either a valid object is returned (id  original), or a 409/400 error.
    let duplicateError: Error | null = null;
    let duplicateResult: IEvidenceArchive | null = null;
    try {
        duplicateResult = await api.functional.securityCompliance.evidenceArchive.post(connection, validInput);
    } catch (err) {
        duplicateError = err as Error;
    }
    if (duplicateError) {
        // Acceptable if deduplication or policy violation (409 Conflict/400 BadRequest)
        TestValidator.predicate("Duplicate triggers error with code 409 or 400")(
            (duplicateError as any).status === 400 || (duplicateError as any).status === 409
        );
    } else if (duplicateResult) {
        typia.assert<IEvidenceArchive>(duplicateResult);
        TestValidator.predicate("Duplicate returns different id")(duplicateResult.id !== created.id);
    } else {
        throw new Error("Unexpected duplicate evidence POST outcome");
    }
}

