    () => Array.isArray(resNoFilter.data) && resNoFilter.pagination.current === 1,
  );

  // 2. If any client exists, test filter by client_name
  let filterClientName = undefined;
  if (resNoFilter.data.length > 0) {
    filterClientName = resNoFilter.data[0].client_name;
    const reqByName: IApiClients.IRequest = {
      client_name: filterClientName,
      page: 1,
      limit: 5,
    };
    const resByName = await api.functional.apiOmnichannel.apiClients.patch(
      connection,
      reqByName,
    );
    typia.assert<IPageIApiClients>(resByName);
    TestValidator.predicate("all results match client_name filter")(
      () => resByName.data.every((cli) => cli.client_name === filterClientName),
    );
  }

  // 3. Filter by client_name that does NOT exist (edge case)
  const reqNameNotExist: IApiClients.IRequest = {
    client_name: "nonexistent-client-name-xyz",
    page: 1,
    limit: 5,
  };
  const resNameNotExist = await api.functional.apiOmnichannel.apiClients.patch(
    connection,
    reqNameNotExist,
  );
  typia.assert<IPageIApiClients>(resNameNotExist);
  TestValidator.predicate("no matching clients for nonexistent name")(
    () => resNameNotExist.data.length === 0,
  );

  // 4. Filter by is_active true/false (if possible)
  if (resNoFilter.data.length > 0) {
    const isActiveVal = resNoFilter.data[0].is_active;
    const reqActive: IApiClients.IRequest = {
      is_active: isActiveVal,
      page: 1,
      limit: 5,
    };
    const resActive = await api.functional.apiOmnichannel.apiClients.patch(
      connection,
      reqActive,
    );
    typia.assert<IPageIApiClients>(resActive);
    TestValidator.predicate("all results have is_active as expected")(
      () => resActive.data.every((cli) => cli.is_active === isActiveVal),
    );
  }

  // 5. Pagination test
  const reqPage2: IApiClients.IRequest = { page: 2, limit: 2 };
  const resPage2 = await api.functional.apiOmnichannel.apiClients.patch(
    connection,
    reqPage2,
  );
  typia.assert<IPageIApiClients>(resPage2);
  TestValidator.predicate("pagination current page matches request")(
    () => resPage2.pagination.current === 2 && resPage2.pagination.limit === 2,
  );

  // 6. Negative test: invalid parameter (limit 0)
  const reqInvalidLimit: IApiClients.IRequest = { page: 1, limit: 0 };
  await TestValidator.httpError("rejects invalid limit")(400)(async () => {
    await api.functional.apiOmnichannel.apiClients.patch(
      connection,
      reqInvalidLimit,
    );
  });

  // 7. Negative test: invalid parameter (negative page)
  const reqInvalidPage: IApiClients.IRequest = { page: -1, limit: 2 };
  await TestValidator.httpError("rejects negative page")(400)(async () => {
    await api.functional.apiOmnichannel.apiClients.patch(
      connection,
      reqInvalidPage,
    );
  });
}

import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IApiClients } from "@ORGANIZATION/PROJECT-api/lib/structures/IApiClients";
import typia from "typia";

export async function test_api_get_api_client_by_valid_and_invalid_id(connection: api.IConnection): Promise<void> {
    // 1. Create a new API client
    const createInput: IApiClients.ICreate = {
        client_name: `test-client-${Date.now()}`,
        contact_email: `contact-${Date.now()}@example.com`,
        public_key: "MIIBIjANBgkqhkiG9w0BAQEFAAOCAQ8AMIIBCgKCAQEAsimulatedpublickey==",
        description: "Test API client E2E",
    };
    const created: IApiClients = await api.functional.apiOmnichannel.apiClients.post(connection, createInput);
    typia.assert<IApiClients>(created);
    TestValidator.predicate("created.id must be defined")(() => typeof created.id === "string" && !!created.id);

    // 2. Retrieve API client by valid ID
    const found: IApiClients = await api.functional.apiOmnichannel.apiClients.get(connection, created.id);
    typia.assert<IApiClients>(found);
    TestValidator.equals("Get by valid id should match create")((key) => key === "created_at" || key === "deleted_at" || key === "description" || key === "public_key")
        (created)(found);

    // 3. Attempt to fetch with non-existent ID (should return 404)
    const invalidId = "ffffffff-ffff-ffff-ffff-ffffffffffff"; // unlikely UUID
    await TestValidator.httpError("Get by invalid id must fail")(404)(
        () => api.functional.apiOmnichannel.apiClients.get(connection, invalidId)
    );
}

import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IApiClients } from "@ORGANIZATION/PROJECT-api/lib/structures/IApiClients";
import typia from "typia";

export async function test_api_create_api_client_with_required_and_invalid_data(
  connection: api.IConnection,
): Promise<void> {
  // 1. Valid client creation
  const input = {
    client_name: `client_${Date.now()}`,
    contact_email: `test_${Math.floor(Math.random() * 100000)}@example.com`,
    public_key: "-----BEGIN PUBLIC KEY-----\nMIIBIjANBgkqhkiG9w0BAQE...fakekey...IDAQAB\n-----END PUBLIC KEY-----",
    description: "Test integration client"
  };
  const created = await api.functional.apiOmnichannel.apiClients.post(
    connection,
    input,
  );
  typia.assert<IApiClients>(created);
  TestValidator.equals("client_name")(input.client_name)(created.client_name);
  TestValidator.equals("contact_email")(input.contact_email)(created.contact_email);
  TestValidator.equals("public_key")(input.public_key)(created.public_key ?? "");
  TestValidator.equals("description")(input.description)(created.description ?? "");

  // 2. Missing required: client_name
  const inputNoName = {
    contact_email: `test2_${Date.now()}@example.com`,
    public_key: input.public_key
  } as any;
  await TestValidator.httpError("missing client_name")(400)(
    () => api.functional.apiOmnichannel.apiClients.post(connection, inputNoName)
  );

  // 3. Missing required: contact_email
  const inputNoEmail = {
    client_name: `client_no_email_${Date.now()}`,
    public_key: input.public_key
  } as any;
  await TestValidator.httpError("missing contact_email")(400)(
    () => api.functional.apiOmnichannel.apiClients.post(connection, inputNoEmail)
  );

  // 4. Invalid email format
  const inputBadEmail = {
    client_name: `client_bad_email_${Date.now()}`,
    contact_email: "not-an-email",
    public_key: input.public_key,
  };
  await TestValidator.httpError("invalid email format")(400)(
    () => api.functional.apiOmnichannel.apiClients.post(connection, inputBadEmail)
  );

  // 5. Duplicate client_name
  const inputDup = {
    client_name: input.client_name,
    contact_email: `dup_${Math.floor(Math.random() * 10000)}@example.com`,
    public_key: input.public_key,
  };
  await TestValidator.httpError("duplicate client_name")(409, 400, 422)(
    () => api.functional.apiOmnichannel.apiClients.post(connection, inputDup)
  );

  // 6. Duplicate contact_email
  const inputDupEmail = {
    client_name: `dupname_${Date.now()}`,
    contact_email: input.contact_email,
    public_key: input.public_key,
  };
  await TestValidator.httpError("duplicate contact_email")(409, 400, 422)(
    () => api.functional.apiOmnichannel.apiClients.post(connection, inputDupEmail)
  );

  // 7. Forbidden/restricted client_name
  const restrictedNames = ["admin", "system"];
  for (const name of restrictedNames) {
    const restrictedInput = {
      client_name: name,
      contact_email: `r${name}_${Date.now()}@example.com`,
      public_key: input.public_key,
    };
    await TestValidator.httpError("restricted client_name")(400, 403, 422)(
      () => api.functional.apiOmnichannel.apiClients.post(connection, restrictedInput)
    );
  }
}
